"use client"

import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { api } from "../../utils/api"
import { useAuth } from "../../contexts/AuthContext"
import { useToast } from "../../contexts/ToastContext"
import Layout from "../../components/layout/Layout"
import "./StoryPages.css"

const CreateStoryPage = () => {
  const { currentUser } = useAuth()
  const { showToast } = useToast()
  const navigate = useNavigate()

  const [formData, setFormData] = useState({
    title: "",
    content: "",
    media: null,
  })

  const [mediaPreview, setMediaPreview] = useState("")
  const [loading, setLoading] = useState(false)

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({ ...prev, [name]: value }))
  }

  const handleFileChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      // Check if file is image or video
      if (!file.type.match("image.*") && !file.type.match("video.*")) {
        showToast("Please upload an image or video file", "error")
        return
      }

      setFormData((prev) => ({ ...prev, media: file }))

      // Create media preview
      const reader = new FileReader()
      reader.onloadend = () => {
        setMediaPreview(reader.result)
      }
      reader.readAsDataURL(file)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!currentUser) {
      showToast("Please login to create a story", "error")
      navigate("/login")
      return
    }

    if (!formData.media) {
      showToast("Please upload an image or video for your story", "warning")
      return
    }

    setLoading(true)

    try {
      const storyData = new FormData()
      storyData.append("title", formData.title)
      storyData.append("content", formData.content)
      storyData.append("media", formData.media)

      await api.post("/stories", storyData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })

      showToast("Story created successfully!", "success")
      navigate("/stories")
    } catch (error) {
      console.error("Error creating story:", error)
      showToast(error.response?.data?.message || "Failed to create story", "error")
      setLoading(false)
    }
  }

  return (
    <Layout>
      <div className="create-story-container">
        <h1>Create a New Story</h1>

        <form onSubmit={handleSubmit} className="create-story-form">
          <div className="form-group">
            <label htmlFor="title">Title (Optional)</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="Give your story a title"
            />
          </div>

          <div className="form-group">
            <label htmlFor="content">Content (Optional)</label>
            <textarea
              id="content"
              name="content"
              value={formData.content}
              onChange={handleChange}
              placeholder="Add a description or content for your story"
              rows="3"
            />
          </div>

          <div className="form-group">
            <label htmlFor="media">Upload Media (Required)</label>
            <input type="file" id="media" name="media" onChange={handleFileChange} accept="image/*,video/*" required />

            {mediaPreview && (
              <div className="media-preview">
                {formData.media.type.startsWith("image/") ? (
                  <img src={mediaPreview || "/placeholder.svg"} alt="Story preview" />
                ) : formData.media.type.startsWith("video/") ? (
                  <video controls src={mediaPreview} />
                ) : null}
              </div>
            )}
          </div>

          <div className="form-actions">
            <button type="button" onClick={() => navigate("/stories")} className="cancel-button" disabled={loading}>
              Cancel
            </button>
            <button type="submit" className="submit-button" disabled={loading}>
              {loading ? "Creating Story..." : "Create Story"}
            </button>
          </div>
        </form>
      </div>
    </Layout>
  )
}

export default CreateStoryPage
