import { formatMediaUrl } from "../../utils/mediaUtils"

const MediaDebugger = ({ media }) => {
  if (!media || !Array.isArray(media) || media.length === 0) {
    return (
      <div className="debug-panel">
        <h3>Media Debug Info</h3>
        <p>No media available</p>
      </div>
    )
  }

  return (
    <div
      className="debug-panel"
      style={{
        border: "1px solid #ccc",
        padding: "10px",
        margin: "10px 0",
        backgroundColor: "#f8f9fa",
        fontSize: "12px",
      }}
    >
      <h3>Media Debug Info</h3>
      {media.map((item, index) => (
        <div key={index} style={{ marginBottom: "10px", borderBottom: "1px dashed #ccc", paddingBottom: "10px" }}>
          <p>
            <strong>Media #{index + 1}</strong>
          </p>
          <p>ID: {item.id || "N/A"}</p>
          <p>Type: {item.type || "N/A"}</p>
          <p>Original URL: {item.url || "N/A"}</p>
          <p>Formatted URL: {formatMediaUrl(item.url) || "N/A"}</p>
          <div>
            <p>Preview:</p>
            <img
              src={formatMediaUrl(item.url) || "/placeholder.svg"}
              alt="Media preview"
              style={{ maxWidth: "200px", maxHeight: "200px", border: "1px solid #ddd" }}
              onError={(e) => {
                e.target.src = "/placeholder.svg?height=200&width=200"
                e.target.alt = "Failed to load image"
              }}
            />
          </div>
        </div>
      ))}
    </div>
  )
}

export default MediaDebugger
