"use client"
import { useState, useRef } from "react"
import { useNavigate } from "react-router-dom"
import { postAPI } from "../../utils/api"
import { useAuth } from "../../contexts/AuthContext"
import { useToast } from "../../contexts/ToastContext"
import Layout from "../../components/layout/Layout"
import LoadingSpinner from "../../components/common/LoadingSpinner"
import "./CreatePostPage.css"

const CreatePostPage = () => {
  const { currentUser } = useAuth()
  const { showToast } = useToast()
  const navigate = useNavigate()
  const fileInputRef = useRef(null)

  const [formData, setFormData] = useState({
    title: "",
    content: "",
    postType: "DISCUSSION",
    media: null,
  })

  const [mediaPreview, setMediaPreview] = useState("")
  const [loading, setLoading] = useState(false)
  const [mediaUploadProgress, setMediaUploadProgress] = useState(0)

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData(prev => ({ ...prev, [name]: value }))
  }

  const handleFileChange = (e) => {
    const file = e.target.files[0]
    if (!file) return

    const validTypes = ["image/jpeg", "image/png", "image/gif", "video/mp4", "video/webm"]
    const maxSize = 10 * 1024 * 1024 // 10MB

    if (!validTypes.includes(file.type)) {
      showToast("Only images (JPEG, PNG, GIF) and videos (MP4, WebM) are allowed", "error")
      return
    }

    if (file.size > maxSize) {
      showToast("File size must be less than 10MB", "error")
      return
    }

    setFormData(prev => ({ ...prev, media: file }))

    const reader = new FileReader()
    reader.onloadend = () => setMediaPreview(reader.result)
    reader.readAsDataURL(file)
  }

  const removeMedia = () => {
    setFormData(prev => ({ ...prev, media: null }))
    setMediaPreview("")
    if (fileInputRef.current) {
      fileInputRef.current.value = ""
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!currentUser) {
      showToast("Please login to create a post", "error")
      navigate("/login")
      return
    }

    setLoading(true)
    setMediaUploadProgress(0)

    try {
      const { title, content, postType, media } = formData

      const postRequest = {
        title,
        content,
        postType,
      }

      const formDataToSend = new FormData()
      formDataToSend.append(
        "post",
        new Blob([JSON.stringify(postRequest)], { type: "application/json" })
      )

      if (media) {
        formDataToSend.append("files", media)
      }

      const config = {
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          )
          setMediaUploadProgress(percentCompleted)
        }
      }

      const response = await postAPI.createPost(formDataToSend, config)

      if (response.data?.success) {
        showToast("Post created successfully!", "success")
        navigate(`/posts/${response.data.data.id}`)
      }
    } catch (error) {
      console.error("Error creating post:", error)
      showToast(
        error.response?.data?.message || 
        "Failed to create post", 
        "error"
      )
    } finally {
      setLoading(false)
      setMediaUploadProgress(0)
    }
  }

  const confirmCancel = () => {
    if (formData.title || formData.content || formData.media) {
      if (window.confirm("Are you sure you want to discard this post?")) {
        navigate("/")
      }
    } else {
      navigate("/")
    }
  }

  return (
    <Layout showSidebar={false}>
      <div className="create-post-container">
        <h1>Create a New Post</h1>

        <form onSubmit={handleSubmit} className="create-post-form">
          <div className="form-group">
            <label htmlFor="postType">Post Type</label>
            <select
              id="postType"
              name="postType"
              value={formData.postType}
              onChange={handleChange}
              required
            >
              <option value="DISCUSSION">Discussion</option>
              <option value="QUESTION">Question</option>
              <option value="RESOURCE">Resource</option>
              <option value="PROJECT">Project</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="title">Title *</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="Enter a descriptive title"
              maxLength={100}
              minLength={5}
              required
            />
            <small className="character-count">
              {formData.title.length}/100 (min 5)
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="content">Content *</label>
            <textarea
              id="content"
              name="content"
              value={formData.content}
              onChange={handleChange}
              placeholder="Share your knowledge, ask a question, or start a discussion"
              rows="8"
              maxLength={2000}
              minLength={10}
              required
            />
            <small className="character-count">
              {formData.content.length}/2000 (min 10)
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="media">Add Media (Optional)</label>
            <input
              type="file"
              id="media"
              name="media"
              ref={fileInputRef}
              onChange={handleFileChange}
              accept="image/*,video/*"
            />
            <small className="file-hint">
              Max 10MB. Supported: JPEG, PNG, GIF, MP4, WebM
            </small>

            {mediaPreview && (
              <div className="media-preview">
                {formData.media.type.startsWith("image/") ? (
                  <div className="preview-container">
                    <img src={mediaPreview} alt="Preview" />
                    <button 
                      type="button" 
                      className="remove-media-btn"
                      onClick={removeMedia}
                      aria-label="Remove media"
                    >
                      ×
                    </button>
                  </div>
                ) : formData.media.type.startsWith("video/") ? (
                  <div className="preview-container">
                    <video controls src={mediaPreview} />
                    <button 
                      type="button" 
                      className="remove-media-btn"
                      onClick={removeMedia}
                      aria-label="Remove media"
                    >
                      ×
                    </button>
                  </div>
                ) : null}
              </div>
            )}

            {mediaUploadProgress > 0 && mediaUploadProgress < 100 && (
              <div className="upload-progress">
                <progress 
                  value={mediaUploadProgress} 
                  max="100"
                  aria-label="Media upload progress"
                />
                <span>{mediaUploadProgress}%</span>
              </div>
            )}
          </div>

          <div className="form-actions">
            <button
              type="button"
              onClick={confirmCancel}
              className="cancel-button"
              disabled={loading}
            >
              Cancel
            </button>
            <button 
              type="submit" 
              className="submit-button" 
              disabled={
                loading || 
                formData.title.length < 5 || 
                formData.content.length < 10
              }
              aria-busy={loading}
            >
              {loading ? (
                <>
                  <LoadingSpinner size="small" />
                  Creating Post...
                </>
              ) : (
                "Create Post"
              )}
            </button>
          </div>
        </form>
      </div>
    </Layout>
  )
}

export default CreatePostPage
