"use client"
import { useState, useEffect, useCallback } from "react"
import { Link } from "react-router-dom"
import { postAPI } from "../utils/api"
import PostItem from "../components/posts/PostItem"
import StoryCircle from "../components/stories/StoryCircle"
import { useAuth } from "../contexts/AuthContext"
import { storyAPI } from "../utils/api"
import LoadingSpinner from "../components/common/LoadingSpinner"
import ErrorMessage from "../components/common/ErrorMessage"
import usePagination from "../hooks/usePagination"
import "./HomePage.css"

const HomePage = () => {
  const { currentUser } = useAuth()
  const [posts, setPosts] = useState([])
  const [stories, setStories] = useState([])
  const [error, setError] = useState(null)

  const fetchPosts = useCallback(async (page) => {
    try {
      const response = await postAPI.getFeedPosts(page)
      const newPosts = response?.data?.data?.content || []

      if (newPosts.length === 0) {
        pagination.setHasMore(false)
      } else {
        setPosts(prevPosts => 
          page === 0 ? newPosts : [...prevPosts, ...newPosts]
        )
      }
    } catch (err) {
      console.error("Error fetching posts:", err)
      setError(err.response?.data?.message || "Failed to load posts. Please try again.")
    }
  }, [])

  const pagination = usePagination(0, fetchPosts)

  const fetchStories = useCallback(async () => {
    try {
      const response = await storyAPI.getFeedStories()
      setStories(response?.data?.data || [])
    } catch (err) {
      console.error("Error fetching stories:", err)
    }
  }, [])

  useEffect(() => {
    fetchStories()
  }, [fetchStories])

  const handleRefresh = async () => {
    setError(null)
    pagination.reset()
    await fetchPosts(0)
    await fetchStories()
  }

  return (
    <div className="home-page">
      <div className="home-header">
        <h1>Feed</h1>
        <Link to="/posts/create" className="btn btn-primary">
          <i className="material-icons">add</i> Create Post
        </Link>
      </div>

      <div className="stories-container">
        <StoryCircle user={currentUser} isCreate={true} key="create-story" />

        {Array.isArray(stories) && stories.map((story) => (
          <StoryCircle 
            key={story.id} 
            story={story} 
            user={story?.user}
          />
        ))}
      </div>

      <div className="posts-container">
        {error && (
          <ErrorMessage 
            message={error} 
            onRetry={handleRefresh}
          />
        )}

        {pagination.isInitialLoading ? (
          <LoadingSpinner />
        ) : (
          <>
            {posts.length > 0 ? (
              <>
                {posts.map(post => (
                  <PostItem 
                    key={post.id} 
                    post={post} 
                  />
                ))}

                {pagination.isLoading && (
                  <LoadingSpinner size="small" />
                )}

                {!pagination.isLoading && pagination.hasMore && (
                  <button 
                    className="load-more-btn" 
                    onClick={pagination.loadMore}
                    disabled={pagination.isLoading}
                  >
                    Load More
                  </button>
                )}
              </>
            ) : (
              <div className="empty-state">
                <i className="material-icons">sentiment_dissatisfied</i>
                <h3>No posts yet</h3>
                <p>Follow more users to see their posts or create your own!</p>
                <div className="empty-actions">
                  <Link to="/explore" className="btn btn-secondary">
                    Explore Users
                  </Link>
                  <Link to="/posts/create" className="btn btn-primary">
                    Create a Post
                  </Link>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  )
}

export default HomePage
