"use client"
import { useState, useEffect } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../../contexts/AuthContext"
import { useAuth0 } from "@auth0/auth0-react"
import "./AuthPages.css"

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
    name: "",
  })
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const { register, isAuthenticated } = useAuth()
  const { loginWithRedirect, isAuthenticated: isAuth0Authenticated, user: auth0User } = useAuth0()
  const navigate = useNavigate()

  // Handle Auth0 authentication
  useEffect(() => {
    const handleAuth0Login = async () => {
      if (isAuth0Authenticated && auth0User) {
        try {
          // Call your backend to handle Auth0 login
          const response = await fetch("/api/auth/auth0-callback", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              auth0Id: auth0User.sub,
              email: auth0User.email,
              name: auth0User.name,
            }),
          })

          if (response.ok) {
            const data = await response.json()
            // Update your auth context with the token and user info
            if (data.data && data.data.token) {
              localStorage.setItem("token", data.data.token)
              window.location.href = "/" // Redirect to home page
            }
          }
        } catch (error) {
          console.error("Error handling Auth0 login:", error)
          setError("Failed to process Auth0 login")
        }
      }
    }

    if (isAuth0Authenticated && auth0User) {
      handleAuth0Login()
    }
  }, [isAuth0Authenticated, auth0User])

  // Redirect if already authenticated
  useEffect(() => {
    if (isAuthenticated) {
      navigate("/")
    }
  }, [isAuthenticated, navigate])

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({
      ...formData,
      [name]: value,
    })
  }

  const validateForm = () => {
    if (!formData.username.trim() || formData.username.length < 3 || formData.username.length > 50) {
      setError("Username must be between 3 and 50 characters")
      return false
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
    if (!emailRegex.test(formData.email)) {
      setError("Please enter a valid email address")
      return false
    }

    if (!formData.password || formData.password.length < 6) {
      setError("Password must be at least 6 characters long")
      return false
    }

    return true
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setError("")

    if (!validateForm()) {
      return
    }

    try {
      setLoading(true)
      const result = await register(formData)

      if (result.success) {
        navigate("/")
      } else {
        setError(result.error || "Failed to register. Please try again.")
      }
    } catch (err) {
      setError("An unexpected error occurred. Please try again.")
      console.error("Registration error:", err)
    } finally {
      setLoading(false)
    }
  }

  const handleGoogleSignUp = () => {
    loginWithRedirect({
      authorizationParams: {
        connection: "google-oauth2",
        screen_hint: "signup",
      },
    })
  }

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h2>Create an Account</h2>
          <p>Join the SkillSUp community</p>
        </div>

        {error && <div className="auth-error">{error}</div>}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-control">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Choose a unique username"
              disabled={loading}
            />
          </div>

          <div className="form-control">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email address"
              disabled={loading}
            />
          </div>

          <div className="form-control">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Create a password (min. 6 characters)"
              disabled={loading}
            />
          </div>

          <div className="form-control">
            <label htmlFor="name">Full Name (Optional)</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your full name"
              disabled={loading}
            />
          </div>

          <button type="submit" className="btn btn-primary w-full" disabled={loading}>
            {loading ? "Creating account..." : "Sign Up"}
          </button>
          <button
            type="button"
            className="btn btn-google w-full"
            style={{ marginTop: "10px" }}
            onClick={handleGoogleSignUp}
            disabled={loading}
          >
            <img src="/google.png" alt="Google" style={{ width: "20px", marginRight: "8px" }} />
            Sign Up with Google
          </button>
        </form>

        <div className="auth-footer">
          <p>
            Already have an account? <Link to="/login">Sign In</Link>
          </p>
        </div>
      </div>
    </div>
  )
}

export default RegisterPage
