"use client"

import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { api } from "../../utils/api"
import { useAuth } from "../../contexts/AuthContext"
import { useToast } from "../../contexts/ToastContext"
import Layout from "../../components/layout/Layout"
import "./CreatePostPage.css"

const CreatePostPage = () => {
  const { currentUser } = useAuth()
  const { showToast } = useToast()
  const navigate = useNavigate()

  const [formData, setFormData] = useState({
    title: "",
    content: "",
    postType: "DISCUSSION", // Default to discussion
    media: null,
  })

  const [mediaPreview, setMediaPreview] = useState("")
  const [loading, setLoading] = useState(false)

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({ ...prev, [name]: value }))
  }

  const handleFileChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setFormData((prev) => ({ ...prev, media: file }))

      // Create media preview
      const reader = new FileReader()
      reader.onloadend = () => {
        setMediaPreview(reader.result)
      }
      reader.readAsDataURL(file)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!currentUser) {
      showToast("Please login to create a post", "error")
      navigate("/login")
      return
    }

    setLoading(true)

    try {
      const postData = new FormData()
      postData.append("title", formData.title)
      postData.append("content", formData.content)
      postData.append("postType", formData.postType)

      if (formData.media) {
        postData.append("media", formData.media)
      }

      const response = await api.post("/posts", postData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })

      showToast("Post created successfully!", "success")
      navigate(`/posts/${response.data.id}`)
    } catch (error) {
      console.error("Error creating post:", error)
      showToast(error.response?.data?.message || "Failed to create post", "error")
      setLoading(false)
    }
  }

  return (
    <Layout>
      <div className="create-post-container">
        <h1>Create a New Post</h1>

        <form onSubmit={handleSubmit} className="create-post-form">
          <div className="form-group">
            <label htmlFor="postType">Post Type</label>
            <select id="postType" name="postType" value={formData.postType} onChange={handleChange} required>
              <option value="DISCUSSION">Discussion</option>
              <option value="QUESTION">Question</option>
              <option value="RESOURCE">Resource</option>
              <option value="PROJECT">Project</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="title">Title</label>
            <input
              type="text"
              id="title"
              name="title"
              value={formData.title}
              onChange={handleChange}
              placeholder="Enter a descriptive title"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="content">Content</label>
            <textarea
              id="content"
              name="content"
              value={formData.content}
              onChange={handleChange}
              placeholder="Share your knowledge, ask a question, or start a discussion"
              rows="8"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="media">Add Media (Optional)</label>
            <input type="file" id="media" name="media" onChange={handleFileChange} accept="image/*,video/*" />

            {mediaPreview && (
              <div className="media-preview">
                {formData.media.type.startsWith("image/") ? (
                  <img src={mediaPreview || "/placeholder.svg"} alt="Preview" />
                ) : formData.media.type.startsWith("video/") ? (
                  <video controls src={mediaPreview} />
                ) : null}
              </div>
            )}
          </div>

          <div className="form-actions">
            <button type="button" onClick={() => navigate("/")} className="cancel-button" disabled={loading}>
              Cancel
            </button>
            <button type="submit" className="submit-button" disabled={loading}>
              {loading ? "Creating Post..." : "Create Post"}
            </button>
          </div>
        </form>
      </div>
    </Layout>
  )
}

export default CreatePostPage
