"use client"

import { useState, useEffect } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../../contexts/AuthContext"
import { userAPI } from "../../utils/api"
import "./Sidebar.css"

const Sidebar = () => {
  const { currentUser } = useAuth()
  const [suggestedUsers, setSuggestedUsers] = useState([])
  const [loading, setLoading] = useState(true)
  const navigate = useNavigate()

  useEffect(() => {
    fetchSuggestedUsers()
  }, [])

  const fetchSuggestedUsers = async () => {
    try {
      setLoading(true)
      const response = await userAPI.getSuggestedUsers(5)
      setSuggestedUsers(response.data.data)
    } catch (error) {
      console.error("Error fetching suggested users:", error)
    } finally {
      setLoading(false)
    }
  }

  const handleFollow = async (userId) => {
    try {
      await userAPI.followUser(userId)
      // Update the isFollowing property for this user
      setSuggestedUsers(suggestedUsers.map((user) => (user.id === userId ? { ...user, isFollowing: true } : user)))
    } catch (error) {
      console.error("Error following user:", error)
    }
  }

  return (
    <div className="sidebar">
      {currentUser && (
        <div className="sidebar-profile">
          <Link to={`/profile/${currentUser.username}`}>
            <img
              src={currentUser.avatarUrl || `/placeholder.svg?height=60&width=60`}
              alt={currentUser.username}
              className="avatar avatar-lg"
            />
          </Link>
          <div className="sidebar-profile-info">
            <Link to={`/profile/${currentUser.username}`} className="sidebar-profile-name">
              {currentUser.name || currentUser.username}
            </Link>
            <Link to={`/profile/${currentUser.username}`} className="sidebar-profile-username">
              @{currentUser.username}
            </Link>
          </div>
        </div>
      )}

      <div className="sidebar-nav">
        <Link to="/" className="sidebar-nav-item">
          <i className="material-icons">home</i>
          <span>Home</span>
        </Link>
        <Link to="/explore" className="sidebar-nav-item">
          <i className="material-icons">explore</i>
          <span>Explore</span>
        </Link>
        <Link to="/learning-plans" className="sidebar-nav-item">
          <i className="material-icons">school</i>
          <span>Learning Plans</span>
        </Link>
        <Link to="/posts/create" className="sidebar-nav-item">
          <i className="material-icons">add_circle</i>
          <span>Create Post</span>
        </Link>
        <Link to="/stories/create" className="sidebar-nav-item">
          <i className="material-icons">add_a_photo</i>
          <span>Create Story</span>
        </Link>
        <Link to="/learning-plans/create" className="sidebar-nav-item">
          <i className="material-icons">assignment</i>
          <span>Create Learning Plan</span>
        </Link>
      </div>

      <div className="sidebar-section">
        <div className="sidebar-section-header">
          <h3>Suggested Users</h3>
        </div>
        {loading ? (
          <div className="sidebar-loading">Loading...</div>
        ) : (
          <div className="sidebar-users">
            {suggestedUsers.length > 0 ? (
              suggestedUsers.map((user) => (
                <div key={user.id} className="sidebar-user">
                  <Link to={`/profile/${user.username}`} className="sidebar-user-info">
                    <img
                      src={user.avatarUrl || `/placeholder.svg?height=40&width=40`}
                      alt={user.username}
                      className="avatar"
                    />
                    <div>
                      <span className="sidebar-user-name">{user.name || user.username}</span>
                      <span className="sidebar-user-username">@{user.username}</span>
                    </div>
                  </Link>
                  {!user.isFollowing ? (
                    <button className="btn btn-secondary btn-small" onClick={() => handleFollow(user.id)}>
                      Follow
                    </button>
                  ) : (
                    <span className="sidebar-following-badge">Following</span>
                  )}
                </div>
              ))
            ) : (
              <p className="sidebar-empty">No suggestions available</p>
            )}
          </div>
        )}
      </div>
    </div>
  )
}

export default Sidebar
