import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { api } from '../../utils/api';
import { useAuth } from '../../contexts/AuthContext';
import { useToast } from '../../contexts/ToastContext';
import { formatDistanceToNow } from 'date-fns';
import './StoryPages.css';

const StoriesPage = () => {
  const { storyId } = useParams();
  const { currentUser } = useAuth();
  const { showToast } = useToast();
  const navigate = useNavigate();

  const [stories, setStories] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    const fetchStories = async () => {
      try {
        setIsLoading(true);

        let response;
        if (storyId) {
          // Fetch the specific story and related stories
          response = await api.get(`/api/stories/related/${storyId}`);

          // Find the index of the requested story
          const index = response.data.findIndex(story => story.id.toString() === storyId);
          if (index !== -1) {
            setCurrentIndex(index);
          }
        } else {
          // Fetch all stories
          response = await api.get('/api/stories');
        }

        setStories(response.data);
      } catch (error) {
        console.error('Error fetching stories:', error);
        showToast('Failed to load stories', 'error');
      } finally {
        setIsLoading(false);
      }
    };

    fetchStories();
  }, [storyId, showToast]);

  useEffect(() => {
    if (stories.length === 0 || isPaused) return;

    // Auto-advance to next story after 5 seconds
    const timer = setTimeout(() => {
      if (currentIndex < stories.length - 1) {
        setCurrentIndex(currentIndex + 1);
      } else {
        // Navigate back to home when all stories are viewed
        navigate('/');
      }
    }, 5000);

    return () => clearTimeout(timer);
  }, [currentIndex, stories.length, isPaused, navigate]);

  const handleNext = () => {
    if (currentIndex < stories.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else {
      navigate('/');
    }
  };

  const handlePrevious = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  const togglePause = () => {
    setIsPaused(!isPaused);
  };

  if (isLoading) {
    return <div className="loading-container">Loading stories...</div>;
  }

  if (stories.length === 0) {
    return (
      <div className="no-stories-container">
        <p>No stories available</p>
        <button 
          className="back-button"
          onClick={() => navigate('/')}
        >
          Back to Home
        </button>
      </div>
    );
  }

  const currentStory = stories[currentIndex];

  return (
    <div className="stories-page">
      <div className="stories-container">
        <div className="story-header">
          <div className="story-author">
            <div className="author-avatar">
              {currentStory.author.profilePictureUrl ? (
                <img src={currentStory.author.profilePictureUrl || "/placeholder.svg"} alt={currentStory.author.name} />
              ) : (
                <div className="default-avatar">
                  {currentStory.author.name.charAt(0)}
                </div>
              )}
            </div>
            <div className="author-info">
              <span className="author-name">{currentStory.author.name}</span>
              <span className="story-time">
                {formatDistanceToNow(new Date(currentStory.createdAt), { addSuffix: true })}
              </span>
            </div>
          </div>

          <button 
            className="close-button"
            onClick={() => navigate('/')}
          >
            ×
          </button>
        </div>

        <div className="story-progress">
          {stories.map((_, index) => (
            <div 
              key={index} 
              className={`progress-bar ${index < currentIndex ? 'completed' : index === currentIndex ? 'active' : ''}`}
            >
              <div className="progress-fill"></div>
            </div>
          ))}
        </div>

        <div className="story-content" onClick={togglePause}>
          {currentStory.mediaType === 'IMAGE' ? (
            <img src={currentStory.mediaUrl || "/placeholder.svg"} alt="Story" className="story-media" />
          ) : (
            <video 
              src={currentStory.mediaUrl} 
              className="story-media" 
              autoPlay 
              loop 
              muted
            />
          )}

          {currentStory.caption && (
            <div className="story-caption">
              <p>{currentStory.caption}</p>
            </div>
          )}
        </div>

        <div className="story-navigation">
          {currentIndex > 0 && (
            <button 
              className="nav-button prev-button"
              onClick={handlePrevious}
            >
              ‹
            </button>
          )}

          {currentIndex < stories.length - 1 && (
            <button 
              className="nav-button next-button"
              onClick={handleNext}
            >
              ›
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default StoriesPage;
