import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { api } from '../../utils/api';
import { useAuth } from '../../contexts/AuthContext';
import { useToast } from '../../contexts/ToastContext';
import { formatDistanceToNow } from 'date-fns';
import './LearningPlanPages.css';

const LearningPlanPage = () => {
  const { planId } = useParams();
  const { currentUser } = useAuth();
  const { showToast } = useToast();
  const navigate = useNavigate();

  const [learningPlan, setLearningPlan] = useState(null);
  const [topics, setTopics] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTopicId, setActiveTopicId] = useState(null);

  useEffect(() => {
    const fetchLearningPlan = async () => {
      try {
        setIsLoading(true);

        // Get learning plan details
        const planResponse = await api.get(`/api/learning-plans/${planId}`);
        setLearningPlan(planResponse.data);

        // Get topics
        const topicsResponse = await api.get(`/api/learning-plans/${planId}/topics`);
        const sortedTopics = topicsResponse.data.sort((a, b) => a.order - b.order);
        setTopics(sortedTopics);

        // Set first topic as active by default
        if (sortedTopics.length > 0) {
          setActiveTopicId(sortedTopics[0].id);
        }
      } catch (error) {
        console.error('Error fetching learning plan:', error);
        showToast('Failed to load learning plan', 'error');
        navigate('/');
      } finally {
        setIsLoading(false);
      }
    };

    fetchLearningPlan();
  }, [planId, showToast, navigate]);

  const handleDeletePlan = async () => {
    if (!window.confirm('Are you sure you want to delete this learning plan?')) {
      return;
    }

    try {
      await api.delete(`/api/learning-plans/${planId}`);
      showToast('Learning plan deleted successfully', 'success');
      navigate('/');
    } catch (error) {
      console.error('Error deleting learning plan:', error);
      showToast('Failed to delete learning plan', 'error');
    }
  };

  const renderTopicResources = (resources) => {
    if (!resources) return null;

    const resourceLines = resources.split('\n').filter(line => line.trim());

    return (
      <div className="topic-resources">
        <h4>Resources</h4>
        <ul>
          {resourceLines.map((resource, index) => {
            // Check if it's a URL
            const isUrl = resource.startsWith('http://') || resource.startsWith('https://');

            return (
              <li key={index}>
                {isUrl ? (
                  <a href={resource} target="_blank" rel="noopener noreferrer">
                    {resource}
                  </a>
                ) : (
                  resource
                )}
              </li>
            );
          })}
        </ul>
      </div>
    );
  };

  if (isLoading) {
    return <div className="loading-container">Loading learning plan...</div>;
  }

  if (!learningPlan) {
    return <div className="error-container">Learning plan not found</div>;
  }

  const activeTopic = topics.find(topic => topic.id === activeTopicId);
  const isAuthor = currentUser && currentUser.id === learningPlan.author.id;

  return (
    <div className="learning-plan-page">
      <div className="learning-plan-header">
        <div className="header-content">
          <h1>{learningPlan.title}</h1>
          <div className="plan-meta">
            <span className="plan-author">
              Created by{' '}
              <Link to={`/profile/${learningPlan.author.username}`}>
                {learningPlan.author.name}
              </Link>
            </span>
            <span className="plan-date">
              {formatDistanceToNow(new Date(learningPlan.createdAt), { addSuffix: true })}
            </span>
            {!learningPlan.isPublic && (
              <span className="plan-privacy">Private</span>
            )}
          </div>

          {learningPlan.description && (
            <p className="plan-description">{learningPlan.description}</p>
          )}
        </div>

        {isAuthor && (
          <div className="plan-actions">
            <button 
              className="edit-button"
              onClick={() => navigate(`/learning-plans/${planId}/edit`)}
            >
              Edit
            </button>
            <button 
              className="delete-button"
              onClick={handleDeletePlan}
            >
              Delete
            </button>
          </div>
        )}
      </div>

      <div className="learning-plan-content">
        <div className="topics-sidebar">
          <h2>Topics</h2>
          <ul className="topics-list">
            {topics.map((topic, index) => (
              <li 
                key={topic.id} 
                className={`topic-item ${topic.id === activeTopicId ? 'active' : ''}`}
                onClick={() => setActiveTopicId(topic.id)}
              >
                <span className="topic-number">{index + 1}</span>
                <span className="topic-title">{topic.title}</span>
              </li>
            ))}
          </ul>
        </div>

        <div className="topic-content">
          {activeTopic ? (
            <>
              <h2 className="topic-title">{activeTopic.title}</h2>

              {activeTopic.description && (
                <div className="topic-description">
                  <p>{activeTopic.description}</p>
                </div>
              )}

              {renderTopicResources(activeTopic.resources)}
            </>
          ) : (
            <div className="no-topics">
              <p>This learning plan has no topics yet.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default LearningPlanPage;
