import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useToast } from '../../contexts/ToastContext';
import { api } from '../../utils/api';
import './PostPages.css';

const CreatePostPage = () => {
  const { currentUser } = useAuth();
  const { showToast } = useToast();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    content: '',
    type: 'REGULAR',
    media: null
  });

  const [previewUrl, setPreviewUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        media: file
      }));

      // Create preview URL
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.content.trim() && !formData.media) {
      showToast('Please add some content or media to your post', 'error');
      return;
    }

    setIsLoading(true);

    try {
      const postData = new FormData();
      postData.append('content', formData.content);
      postData.append('type', formData.type);

      if (formData.media) {
        postData.append('media', formData.media);
      }

      const response = await api.post('/api/posts', postData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      showToast('Post created successfully!', 'success');
      navigate(`/posts/${response.data.id}`);
    } catch (error) {
      console.error('Error creating post:', error);
      showToast('Failed to create post. Please try again.', 'error');
    } finally {
      setIsLoading(false);
    }
  };

  const postTypes = [
    { value: 'REGULAR', label: 'Regular Post' },
    { value: 'QUESTION', label: 'Question' },
    { value: 'RESOURCE', label: 'Resource' },
    { value: 'PROJECT', label: 'Project' }
  ];

  return (
    <div className="create-post-container">
      <h1>Create a Post</h1>

      <form onSubmit={handleSubmit} className="create-post-form">
        <div className="form-group">
          <label htmlFor="type">Post Type</label>
          <select
            id="type"
            name="type"
            value={formData.type}
            onChange={handleChange}
          >
            {postTypes.map(type => (
              <option key={type.value} value={type.value}>
                {type.label}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="content">Content</label>
          <textarea
            id="content"
            name="content"
            value={formData.content}
            onChange={handleChange}
            rows="6"
            placeholder="What's on your mind?"
          />
        </div>

        <div className="form-group">
          <label htmlFor="media">Add Media (optional)</label>
          <input
            type="file"
            id="media"
            name="media"
            accept="image/*,video/*"
            onChange={handleFileChange}
            className="file-input"
          />

          {previewUrl && (
            <div className="media-preview">
              {formData.media.type.startsWith('image/') ? (
                <img src={previewUrl || "/placeholder.svg"} alt="Preview" />
              ) : formData.media.type.startsWith('video/') ? (
                <video src={previewUrl} controls />
              ) : null}
            </div>
          )}
        </div>

        <div className="form-actions">
          <button 
            type="button" 
            className="cancel-button"
            onClick={() => navigate('/')}
          >
            Cancel
          </button>
          <button 
            type="submit" 
            className="submit-button"
            disabled={isLoading}
          >
            {isLoading ? 'Posting...' : 'Post'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreatePostPage;
