"use client"

import { useState, useEffect } from "react"
import { Link, useNavigate, useLocation } from "react-router-dom"
import { useAuth } from "../../contexts/AuthContext"
import "./AuthPages.css"

const LoginPage = () => {
  const [usernameOrEmail, setUsernameOrEmail] = useState("")
  const [password, setPassword] = useState("")
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const { login } = useAuth()
  const navigate = useNavigate()
  const location = useLocation()

  useEffect(() => {
    // Check for error query parameter (from OAuth failure)
    const queryParams = new URLSearchParams(location.search)
    const errorParam = queryParams.get("error")
    if (errorParam) {
      setError(decodeURIComponent(errorParam))
    }
  }, [location])

  const handleSubmit = async (e) => {
    e.preventDefault()
    setError("")

    // Form validation
    if (!usernameOrEmail.trim() || !password) {
      setError("Please fill in all fields")
      return
    }

    try {
      setLoading(true)
      const result = await login({ usernameOrEmail, password })

      if (result.success) {
        navigate("/")
      } else {
        setError(result.error || "Failed to login. Please check your credentials.")
      }
    } catch (err) {
      setError("An unexpected error occurred. Please try again.")
      console.error("Login error:", err)
    } finally {
      setLoading(false)
    }
  }

  const handleGoogleLogin = () => {
    try {
      const apiUrl = process.env.REACT_APP_API_URL || ""
      console.log("Redirecting to Google OAuth at:", `${apiUrl}/oauth2/authorize/google`)
      window.location.href = `${apiUrl}/oauth2/authorize/google`
    } catch (error) {
      console.error("Error redirecting to Google OAuth:", error)
      setError("Failed to connect to Google authentication service. Please try again.")
    }
  }

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h2>Welcome Back</h2>
          <p>Sign in to your SkillUp account</p>
        </div>

        {error && <div className="auth-error">{error}</div>}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-control">
            <label htmlFor="usernameOrEmail">Username or Email</label>
            <input
              type="text"
              id="usernameOrEmail"
              value={usernameOrEmail}
              onChange={(e) => setUsernameOrEmail(e.target.value)}
              placeholder="Enter your username or email"
              disabled={loading}
            />
          </div>

          <div className="form-control">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              disabled={loading}
            />
          </div>

          <button type="submit" className="btn btn-primary w-full" disabled={loading}>
            {loading ? "Signing in..." : "Sign In"}
          </button>

          <button
            type="button"
            className="btn btn-google w-full"
            style={{ marginTop: "10px" }}
            onClick={handleGoogleLogin}
          >
            <img src="/google.png" alt="Google" style={{ width: "20px", marginRight: "8px" }} />
            Sign in with Google
          </button>
        </form>

        <div className="auth-footer">
          <p>
            Don't have an account? <Link to="/register">Sign Up</Link>
          </p>
        </div>
      </div>
    </div>
  )
}

export default LoginPage
