"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { format } from "date-fns"
import { likeAPI, commentAPI } from "../../utils/api"
import "./PostItem.css"
import { getPlaceholderImage } from "../../utils/placeholderUtils"
import { formatMediaUrl, isImageUrl, isVideoUrl, createDirectImageUrl } from "../../utils/mediaUtils"

const PostItem = ({ post }) => {
  const [isLiked, setIsLiked] = useState(post.liked || false)
  const [likesCount, setLikesCount] = useState(post.likesCount || 0)
  const [showCommentForm, setShowCommentForm] = useState(false)
  const [commentText, setCommentText] = useState("")
  const [comments, setComments] = useState([])
  const [loadingComments, setLoadingComments] = useState(false)
  const [hasLoadedComments, setHasLoadedComments] = useState(false)
  const [showDebug, setShowDebug] = useState(true) // Set to true by default for debugging
  const [media, setMedia] = useState([])
  const [imageLoadErrors, setImageLoadErrors] = useState({})

  useEffect(() => {
    if (post.media && Array.isArray(post.media)) {
      console.log("Post media:", post.media)
      const processedMedia = fixMediaUrls(post.media)
      console.log("Processed media:", processedMedia)
      setMedia(processedMedia)
    }
  }, [post.media])

  const fixMediaUrls = (mediaItems) => {
    if (!mediaItems || !Array.isArray(mediaItems)) return []

    return mediaItems.map((media, index) => {
      if (!media) return media

      // Extract filename from URL if it exists
      const filename = media.url ? media.url.split("/").pop() : null

      return {
        ...media,
        originalUrl: media.url, // Keep the original URL for debugging
        url: formatMediaUrl(media.url),
        directUrl: filename ? createDirectImageUrl(filename) : null,
        index,
      }
    })
  }

  const handleImageError = (e, mediaItem) => {
    console.error(`Error loading image: ${mediaItem.url}`, e)

    // Try the direct URL as a fallback
    if (mediaItem.directUrl && mediaItem.url !== mediaItem.directUrl) {
      console.log(`Trying fallback URL: ${mediaItem.directUrl}`)
      e.target.src = mediaItem.directUrl

      // Update the error state to track which fallback we're using
      setImageLoadErrors((prev) => ({
        ...prev,
        [mediaItem.index]: {
          originalFailed: true,
          fallbackAttempted: true,
        },
      }))
    } else {
      // If direct URL also failed or is the same as the original URL, use placeholder
      console.log("Using placeholder image")
      e.target.src = getPlaceholderImage(200, 200, "Image not available")

      // Update the error state
      setImageLoadErrors((prev) => ({
        ...prev,
        [mediaItem.index]: {
          originalFailed: true,
          fallbackFailed: true,
          usingPlaceholder: true,
        },
      }))
    }
  }

  const handleLike = async () => {
    if (!isLiked) {
      try {
        await likeAPI.likePost(post.id)
        setLikesCount((prevCount) => prevCount + 1)
        setIsLiked(true)
      } catch (error) {
        console.error("Error liking post:", error)
      }
    } else {
      try {
        await likeAPI.unlikePost(post.id)
        setLikesCount((prevCount) => Math.max(0, prevCount - 1))
        setIsLiked(false)
      } catch (error) {
        console.error("Error unliking post:", error)
      }
    }
  }

  const toggleComments = async () => {
    const newState = !showCommentForm
    setShowCommentForm(newState)

    if (newState && !hasLoadedComments) {
      await loadComments()
    }
  }

  const loadComments = async () => {
    try {
      setLoadingComments(true)
      const response = await commentAPI.getCommentsByPostId(post.id)
      if (response.data && response.data.data) {
        setComments(response.data.data.content || [])
      }
      setHasLoadedComments(true)
    } catch (error) {
      console.error("Error loading comments:", error)
    } finally {
      setLoadingComments(false)
    }
  }

  const handleCommentSubmit = async (e) => {
    e.preventDefault()
    if (!commentText.trim()) return

    try {
      const response = await commentAPI.createComment(post.id, { content: commentText })
      if (response.data && response.data.data) {
        setComments([response.data.data, ...comments])
      }
      setCommentText("")
    } catch (error) {
      console.error("Error creating comment:", error)
    }
  }

  const getPostTypeBadgeClass = () => {
    switch (post.type) {
      case "SKILL":
        return "post-type-skill"
      case "PROGRESS":
        return "post-type-progress"
      case "PLAN":
        return "post-type-plan"
      default:
        return ""
    }
  }

  // Update the renderMedia function to use the utility functions for determining media type
  const renderMedia = () => {
    if (!media || !Array.isArray(media) || media.length === 0) {
      return null
    }

    return (
      <div className={`post-media post-media-count-${media.length}`}>
        {media.map((mediaItem, index) => (
          <div key={index} className="post-media-item">
            {isImageUrl(mediaItem.url) || mediaItem.type === "IMAGE" ? (
              <img
                src={mediaItem.url || "/placeholder.svg"}
                alt={`Post media ${index + 1}`}
                onError={(e) => handleImageError(e, mediaItem)}
                className="post-image"
                style={{ maxWidth: "100%", height: "auto" }}
              />
            ) : isVideoUrl(mediaItem.url) || mediaItem.type === "VIDEO" ? (
              <video controls className="post-video" style={{ maxWidth: "100%", height: "auto" }}>
                <source src={mediaItem.url} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
            ) : (
              <div className="media-unsupported">Unsupported media type</div>
            )}
          </div>
        ))}
      </div>
    )
  }

  const renderDebugInfo = () => {
    if (!showDebug) return null

    return (
      <div
        style={{
          border: "1px solid #ccc",
          padding: "10px",
          margin: "10px 0",
          backgroundColor: "#f8f9fa",
          fontSize: "12px",
          overflowX: "auto",
        }}
      >
        <h4>Media Debug Info</h4>
        {media && media.length > 0 ? (
          media.map((item, index) => (
            <div key={index} style={{ marginBottom: "20px", borderBottom: "1px solid #eee", paddingBottom: "10px" }}>
              <p>
                <strong>Media #{index + 1}</strong>
              </p>
              <p>ID: {item.id || "N/A"}</p>
              <p>Type: {item.type || "N/A"}</p>
              <p>Original URL: {item.originalUrl || "N/A"}</p>
              <p>Formatted URL: {item.url || "N/A"}</p>
              <p>Direct URL: {item.directUrl || "N/A"}</p>
              <p>
                Load Status:{" "}
                {imageLoadErrors[index]
                  ? imageLoadErrors[index].usingPlaceholder
                    ? "Failed (using placeholder)"
                    : "Error (tried fallback)"
                  : "OK"}
              </p>

              <div style={{ marginTop: "10px" }}>
                <p>
                  <strong>Test Image Display:</strong>
                </p>
                <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
                  {item.url && (
                    <div>
                      <p>Formatted URL:</p>
                      <img
                        src={item.url || "/placeholder.svg"}
                        alt="Test formatted URL"
                        style={{ maxWidth: "100px", maxHeight: "100px", border: "1px solid #ddd" }}
                        onError={(e) => {
                          e.target.style.display = "none"
                          console.error("Formatted URL failed to load")
                        }}
                      />
                    </div>
                  )}

                  {item.directUrl && (
                    <div>
                      <p>Direct URL:</p>
                      <img
                        src={item.directUrl || "/placeholder.svg"}
                        alt="Test direct URL"
                        style={{ maxWidth: "100px", maxHeight: "100px", border: "1px solid #ddd" }}
                        onError={(e) => {
                          e.target.style.display = "none"
                          console.error("Direct URL failed to load")
                        }}
                      />
                    </div>
                  )}

                  {item.originalUrl && (
                    <div>
                      <p>Original URL:</p>
                      <img
                        src={item.originalUrl || "/placeholder.svg"}
                        alt="Test original URL"
                        style={{ maxWidth: "100px", maxHeight: "100px", border: "1px solid #ddd" }}
                        onError={(e) => {
                          e.target.style.display = "none"
                          console.error("Original URL failed to load")
                        }}
                      />
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))
        ) : (
          <p>No media available</p>
        )}

        <div style={{ marginTop: "15px" }}>
          <h4>Environment Info</h4>
          <p>API URL: {process.env.REACT_APP_API_URL || "Not set (using default: http://localhost:8080)"}</p>
          <p>Current Origin: {window.location.origin}</p>
        </div>
      </div>
    )
  }

  return (
    <div className="post-item">
      <div className="post-header">
        <Link to={`/profile/${post.user.username}`} className="post-user">
          <img
            src={post.user.avatarUrl || "/placeholder.svg?height=40&width=40"}
            alt={post.user.username}
            className="avatar"
          />
          <div className="post-user-info">
            <span className="post-user-name">{post.user.name || post.user.username}</span>
            <span className="post-user-username">@{post.user.username}</span>
          </div>
        </Link>
        <div className="post-meta">
          <span className={`post-type-badge ${getPostTypeBadgeClass()}`}>
            {post.type.charAt(0) + post.type.slice(1).toLowerCase()}
          </span>
          <span className="post-date">{format(new Date(post.createdAt), "MMM d, yyyy")}</span>
        </div>
      </div>

      <div className="post-content">
        <p>{post.content}</p>
      </div>

      {/* Debug toggle button */}
      <button
        onClick={() => setShowDebug(!showDebug)}
        style={{
          marginBottom: "10px",
          padding: "5px 10px",
          backgroundColor: "#f0f0f0",
          border: "1px solid #ccc",
          borderRadius: "4px",
          fontSize: "12px",
        }}
      >
        {showDebug ? "Hide Debug" : "Show Debug"}
      </button>

      {/* Debug info */}
      {renderDebugInfo()}

      {/* Media content */}
      {renderMedia()}

      <div className="post-actions">
        <button className={`post-action-btn ${isLiked ? "liked" : ""}`} onClick={handleLike}>
          <i className="material-icons">{isLiked ? "favorite" : "favorite_border"}</i>
          <span>{likesCount}</span>
        </button>
        <button className="post-action-btn" onClick={toggleComments}>
          <i className="material-icons">chat_bubble_outline</i>
          <span>{post.commentsCount || 0}</span>
        </button>
        <Link to={`/posts/${post.id}`} className="post-action-btn">
          <i className="material-icons">open_in_new</i>
        </Link>
      </div>

      {showCommentForm && (
        <div className="post-comments">
          <form onSubmit={handleCommentSubmit} className="comment-form">
            <input
              type="text"
              placeholder="Write a comment..."
              value={commentText}
              onChange={(e) => setCommentText(e.target.value)}
            />
            <button type="submit" className="btn btn-primary btn-small">
              Post
            </button>
          </form>

          <div className="comments-list">
            {loadingComments ? (
              <div className="comments-loading">Loading comments...</div>
            ) : comments.length > 0 ? (
              comments.map((comment) => (
                <div key={comment.id} className="comment-item">
                  <Link to={`/profile/${comment.user.username}`} className="comment-user">
                    <img
                      src={comment.user.avatarUrl || "/placeholder.svg?height=30&width=30"}
                      alt={comment.user.username}
                      className="avatar avatar-sm"
                    />
                  </Link>
                  <div className="comment-content">
                    <div className="comment-header">
                      <Link to={`/profile/${comment.user.username}`} className="comment-username">
                        {comment.user.name || comment.user.username}
                      </Link>
                      <span className="comment-date">{format(new Date(comment.createdAt), "MMM d, yyyy")}</span>
                    </div>
                    <p>{comment.content}</p>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-comments">No comments yet. Be the first to comment!</div>
            )}

            {post.commentsCount > comments.length && (
              <Link to={`/posts/${post.id}`} className="view-all-comments">
                View all {post.commentsCount} comments
              </Link>
            )}
          </div>
        </div>
      )}
    </div>
  )
}

export default PostItem
