"use client";

import { useState, useEffect } from "react";
import { Link, useSearchParams } from "react-router-dom";
import { userAPI, postAPI } from "../utils/api";
import PostItem from "../components/posts/PostItem";
import { useToast } from "../contexts/ToastContext";
import "./ExplorePage.css";

const ExplorePage = () => {
  const [searchParams] = useSearchParams();
  const query = searchParams.get("q") || "";
  const [activeTab, setActiveTab] = useState("posts");
  const [users, setUsers] = useState([]);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const { addToast } = useToast();

  useEffect(() => {
    if (query) {
      fetchSearchResults();
    } else {
      fetchTrendingContent();
    }
  }, [query, activeTab]);

  const fetchSearchResults = async () => {
    setLoading(true);
    try {
      if (activeTab === "users") {
        const response = await userAPI.searchUsers(query);
        setUsers(response.data.data);
      } else {
        const response = await postAPI.searchPosts(query);
        setPosts(response.data.data.content);
      }
    } catch (error) {
      console.error(`Error searching ${activeTab}:`, error);
      addToast(`Failed to search ${activeTab}`, "error");
    } finally {
      setLoading(false);
    }
  };

  const fetchTrendingContent = async () => {
    setLoading(true);
    try {
      if (activeTab === "users") {
        const response = await userAPI.getSuggestedUsers(10);
        setUsers(response.data.data);
      } else {
        const response = await postAPI.getAllPosts();
        setPosts(response.data.data.content);
      }
    } catch (error) {
      console.error(`Error fetching trending ${activeTab}:`, error);
      addToast(`Failed to load trending ${activeTab}`, "error");
    } finally {
      setLoading(false);
    }
  };

  const handleFollow = async (userId) => {
    try {
      const response = await userAPI.followUser(userId);
      if (response.status === 200) {
        setUsers((prevUsers) =>
          prevUsers.map((user) =>
            user.id === userId ? { ...user, isFollowing: true } : user
          )
        );
        addToast("User followed successfully", "success");
      }
    } catch (error) {
      console.error("Error following user:", error);
      addToast("Failed to follow user", "error");
    }
  };

  const handleUnfollow = async (userId) => {
    try {
      const response = await userAPI.unfollowUser(userId);
      if (response.status === 200) {
        setUsers((prevUsers) =>
          prevUsers.map((user) =>
            user.id === userId ? { ...user, isFollowing: false } : user
          )
        );
        addToast("User unfollowed successfully", "success");
      }
    } catch (error) {
      console.error("Error unfollowing user:", error);
      addToast("Failed to unfollow user", "error");
    }
  };

  return (
    <div className="explore-page">
      <div className="explore-header">
        <h1>{query ? `Search Results for "${query}"` : "Explore"}</h1>
      </div>

      <div className="explore-tabs">
        <button
          className={`explore-tab ${activeTab === "posts" ? "active" : ""}`}
          onClick={() => setActiveTab("posts")}
        >
          Posts
        </button>
        <button
          className={`explore-tab ${activeTab === "users" ? "active" : ""}`}
          onClick={() => setActiveTab("users")}
        >
          Users
        </button>
      </div>

      {loading ? (
        <div className="loading-state">
          <div className="loading-spinner"></div>
          <p>Loading...</p>
        </div>
      ) : (
        <>
          {activeTab === "users" ? (
            <div className="users-grid">
              {users.length > 0 ? (
                users.map((user) => (
                  <div key={user.id} className="user-card">
                    <Link to={`/profile/${user.username}`} className="user-card-header">
                      <img
                        src={user.avatarUrl || `/placeholder.svg?height=80&width=80`}
                        alt={user.username}
                        className="avatar avatar-lg"
                      />
                      <div className="user-card-info">
                        <h3>{user.name || user.username}</h3>
                        <p>{user.bio || "No bio available"}</p>
                      </div>
                    </Link>
                    <button
                      onClick={() =>
                        user.isFollowing ? handleUnfollow(user.id) : handleFollow(user.id)
                      }
                      className={`follow-btn ${user.isFollowing ? "unfollow" : "follow"}`}
                    >
                      {user.isFollowing ? "Unfollow" : "Follow"}
                    </button>
                  </div>
                ))
              ) : (
                <p>No users found</p>
              )}
            </div>
          ) : (
            <div className="posts-grid">
              {posts.length > 0 ? (
                posts.map((post) => (
                  <PostItem key={post.id} post={post} />
                ))
              ) : (
                <p>No posts found</p>
              )}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ExplorePage;
