"use client";

import { createContext, useState, useContext, useEffect } from "react";
import { api } from "../utils/api";

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const isAuthenticated = !!token;

  useEffect(() => {
    if (token && !currentUser) {
      // NEW: Set currentUser from login/register data instead of loadUser
      const tokenData = parseJwt(token);
      if (tokenData?.sub) {
        setCurrentUser({ username: tokenData.sub });
      }
      setLoading(false);
    } else {
      setLoading(false);
    }
  }, [token]);

  const setAuthToken = (token) => {
    if (token) {
      localStorage.setItem("token", token);
      api.defaults.headers.common["Authorization"] = `Bearer ${token}`;
    } else {
      localStorage.removeItem("token");
      delete api.defaults.headers.common["Authorization"];
    }
  };

  const login = async (credentials) => {
    try {
      const res = await api.post("/api/auth/login", credentials);
      if (!res.data.success) {
        throw new Error(res.data.message || "Login failed");
      }
      const { token, user } = res.data.data;
      setToken(token);
      setAuthToken(token);
      setCurrentUser(user); // CHANGED: Use user from response
      setError(null);
      return { success: true };
    } catch (err) {
      console.error("Login error:", err);
      const errorMessage = err.response?.data?.message || err.message || "Failed to login";
      setError(errorMessage);
      return { success: false, error: errorMessage };
    }
  };

  const register = async (userData) => {
    try {
      const res = await api.post("/api/auth/register", userData);
      if (!res.data.success) {
        throw new Error(res.data.message || "Registration failed");
      }
      const { token, user } = res.data.data;
      setToken(token);
      setAuthToken(token);
      setCurrentUser(user); // CHANGED: Use user from response
      setError(null);
      return { success: true };
    } catch (err) {
      console.error("Register error:", err);
      const errorMessage = err.response?.data?.message || err.message || "Failed to register";
      setError(errorMessage);
      return { success: false, error: errorMessage };
    }
  };

  const logout = () => {
    setToken(null);
    setCurrentUser(null);
    setAuthToken(null);
    setError(null);
  };

  const updateUser = async (userData) => {
    try {
      const res = await api.put(`/api/users/${currentUser.id}`, userData);
      setCurrentUser(res.data.data);
      return { success: true };
    } catch (err) {
      console.error("Update user error:", err);
      const errorMessage = err.response?.data?.message || "Failed to update user";
      setError(errorMessage);
      return { success: false, error: errorMessage };
    }
  };

  const updateCurrentUser = (user) => {
    setCurrentUser(user);
  };

  const parseJwt = (token) => {
    try {
      if (!token) return null;
      const base64Url = token.split(".")[1];
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const jsonPayload = decodeURIComponent(
        atob(base64)
          .split("")
          .map((c) => "%" + ("00" + c.charCodeAt(0).toString(16)).slice(-2))
          .join("")
      );
      return JSON.parse(jsonPayload);
    } catch (e) {
      console.error("Error parsing JWT:", e);
      return null;
    }
  };

  const value = {
    currentUser,
    isAuthenticated,
    loading,
    error,
    login,
    register,
    logout,
    updateUser,
    updateCurrentUser,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
