"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { postAPI } from "../utils/api"
import PostItem from "../components/posts/PostItem"
import StoryCircle from "../components/stories/StoryCircle"
import { useAuth } from "../contexts/AuthContext"
import { storyAPI } from "../utils/api"
import "./HomePage.css"

const HomePage = () => {
  const { currentUser } = useAuth()
  const [posts, setPosts] = useState([])
  const [stories, setStories] = useState([])
  const [loading, setLoading] = useState(true)
  const [postsLoading, setPostsLoading] = useState(false)
  const [error, setError] = useState(null)
  const [page, setPage] = useState(0)
  const [hasMore, setHasMore] = useState(true)

  useEffect(() => {
    fetchPosts()
    fetchStories()
  }, [])

  useEffect(() => {
    if (page > 0) {
      fetchPosts()
    }
  }, [page])

  const fetchPosts = async () => {
    try {
      page === 0 ? setLoading(true) : setPostsLoading(true)
      const response = await postAPI.getFeedPosts(page)

      // Safely access nested data with optional chaining
      const newPosts = response?.data?.data?.content || []

      if (newPosts.length === 0) {
        setHasMore(false)
      } else {
        setPosts(prevPosts => 
          page === 0 ? newPosts : [...prevPosts, ...newPosts]
        )
      }
    } catch (err) {
      console.error("Error fetching posts:", err)
      setError(err.response?.data?.message || "Failed to load posts. Please try again.")
    } finally {
      setLoading(false)
      setPostsLoading(false)
    }
  }

  const fetchStories = async () => {
    try {
      const response = await storyAPI.getFeedStories()
      // Safely access nested data with optional chaining
      setStories(response?.data?.data || [])
    } catch (err) {
      console.error("Error fetching stories:", err)
      // Don't show error for stories as they're secondary content
    }
  }

  const loadMorePosts = () => {
    if (!postsLoading && hasMore) {
      setPage(prevPage => prevPage + 1)
    }
  }

  return (
    <div className="home-page">
      <div className="home-header">
        <h1>Feed</h1>
        <Link to="/posts/create" className="btn btn-primary">
          <i className="material-icons">add</i> Create Post
        </Link>
      </div>

      {/* Stories section with safe rendering */}
      <div className="stories-container">
        <StoryCircle user={currentUser} isCreate={true} key="create-story" />

        {Array.isArray(stories) && stories.map((story) => (
          <StoryCircle 
            key={story.id} 
            story={story} 
            user={story?.user}  // Safely access user
          />
        ))}
      </div>

      {/* Posts section with comprehensive states */}
      <div className="posts-container">
        {error && (
          <div className="error-message">
            <i className="material-icons">error_outline</i>
            {error}
            <button onClick={() => {
              setError(null)
              fetchPosts()
            }} className="btn btn-small">
              Retry
            </button>
          </div>
        )}

        {loading ? (
          <div className="loading-state">
            <div className="loading-spinner"></div>
            <p>Loading posts...</p>
          </div>
        ) : (
          <>
            {posts.length > 0 ? (
              <>
                {posts.map(post => (
                  <PostItem 
                    key={post.id} 
                    post={post} 
                  />
                ))}

                {postsLoading && (
                  <div className="loading-more">
                    <div className="small-spinner"></div>
                  </div>
                )}

                {!postsLoading && hasMore && (
                  <button 
                    className="load-more-btn" 
                    onClick={loadMorePosts}
                    disabled={postsLoading}
                  >
                    Load More
                  </button>
                )}
              </>
            ) : (
              <div className="empty-state">
                <i className="material-icons">sentiment_dissatisfied</i>
                <h3>No posts yet</h3>
                <p>Follow more users to see their posts or create your own!</p>
                <div className="empty-actions">
                  <Link to="/explore" className="btn btn-secondary">
                    Explore Users
                  </Link>
                  <Link to="/posts/create" className="btn btn-primary">
                    Create a Post
                  </Link>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  )
}

export default HomePage
