"use client"

import { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../../contexts/AuthContext"
import { GoogleLogin } from "@react-oauth/google" // Updated import
import "./AuthPages.css"

const LoginPage = () => {
  const [usernameOrEmail, setUsernameOrEmail] = useState("")
  const [password, setPassword] = useState("")
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const { login, loginWithGoogle } = useAuth()
  const navigate = useNavigate()

  const handleSubmit = async (e) => {
    e.preventDefault()
    setError("")

    // Form validation
    if (!usernameOrEmail.trim() || !password) {
      setError("Please fill in all fields")
      return
    }

    try {
      setLoading(true)
      const result = await login({ usernameOrEmail, password })

      if (result.success) {
        navigate("/")
      } else {
        setError(result.error || "Failed to login. Please check your credentials.")
      }
    } catch (err) {
      setError("An unexpected error occurred. Please try again.")
      console.error("Login error:", err)
    } finally {
      setLoading(false)
    }
  }

  const handleGoogleSuccess = async (response) => {
    try {
      // The response object from Google OAuth has a credential property
      // which is the ID token we need to send to our backend
      const idToken = response.credential

      if (!idToken) {
        setError("Failed to get credentials from Google")
        return
      }

      const result = await loginWithGoogle({ tokenId: idToken })

      if (result.success) {
        navigate("/")
      } else {
        setError(result.error || "Failed to login with Google")
      }
    } catch (error) {
      console.error("Google login error:", error)
      setError("An error occurred during Google login")
    }
  }

  const handleGoogleFailure = (error) => {
    setError("Google login failed. Please try again.")
    console.error("Google login error:", error)
  }

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h2>Welcome Back</h2>
          <p>Sign in to your SkillUp account</p>
        </div>

        {error && <div className="auth-error">{error}</div>}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-control">
            <label htmlFor="usernameOrEmail">Username or Email</label>
            <input
              type="text"
              id="usernameOrEmail"
              value={usernameOrEmail}
              onChange={(e) => setUsernameOrEmail(e.target.value)}
              placeholder="Enter your username or email"
              disabled={loading}
            />
          </div>

          <div className="form-control">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              disabled={loading}
            />
          </div>

          <button type="submit" className="btn btn-primary w-full" disabled={loading}>
            {loading ? "Signing in..." : "Sign In"}
          </button>

          {/* Google Login Button */}
          <GoogleLogin
            onSuccess={handleGoogleSuccess}
            onError={handleGoogleFailure}
            useOneTap
            text="Sign in with Google"
            theme="outline"
            shape="pill"
            size="large"
            width="100%"
            style={{ marginTop: "10px" }}
          />
        </form>

        <div className="auth-footer">
          <p>
            Don't have an account? <Link to="/register">Sign Up</Link>
          </p>
        </div>
      </div>
    </div>
  )
}

export default LoginPage
