import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { api } from '../../utils/api';
import { useAuth } from '../../contexts/AuthContext';
import { useToast } from '../../contexts/ToastContext';
import { formatDistanceToNow } from 'date-fns';
import './PostPages.css';

const PostDetailPage = () => {
  const { postId } = useParams();
  const { currentUser } = useAuth();
  const { showToast } = useToast();
  const navigate = useNavigate();

  const [post, setPost] = useState(null);
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    const fetchPostAndComments = async () => {
      try {
        setIsLoading(true);

        // Get post details
        const postResponse = await api.get(`/api/posts/${postId}`);
        setPost(postResponse.data);

        // Get comments
        const commentsResponse = await api.get(`/api/posts/${postId}/comments`);
        setComments(commentsResponse.data);
      } catch (error) {
        console.error('Error fetching post details:', error);
        showToast('Failed to load post', 'error');
      } finally {
        setIsLoading(false);
      }
    };

    fetchPostAndComments();
  }, [postId, showToast]);

  const handleLike = async () => {
    try {
      if (post.likedByCurrentUser) {
        await api.delete(`/api/posts/${postId}/unlike`);
        setPost(prev => ({
          ...prev,
          likedByCurrentUser: false,
          likesCount: prev.likesCount - 1
        }));
      } else {
        await api.post(`/api/posts/${postId}/like`);
        setPost(prev => ({
          ...prev,
          likedByCurrentUser: true,
          likesCount: prev.likesCount + 1
        }));
      }
    } catch (error) {
      console.error('Error toggling like:', error);
      showToast('Failed to update like status', 'error');
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();

    if (!commentText.trim()) {
      showToast('Comment cannot be empty', 'error');
      return;
    }

    setIsSubmitting(true);

    try {
      const response = await api.post(`/api/posts/${postId}/comments`, {
        content: commentText
      });

      setComments(prev => [response.data, ...prev]);
      setCommentText('');
      setPost(prev => ({
        ...prev,
        commentsCount: prev.commentsCount + 1
      }));

      showToast('Comment added successfully', 'success');
    } catch (error) {
      console.error('Error adding comment:', error);
      showToast('Failed to add comment', 'error');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDeletePost = async () => {
    if (!window.confirm('Are you sure you want to delete this post?')) {
      return;
    }

    try {
      await api.delete(`/api/posts/${postId}`);
      showToast('Post deleted successfully', 'success');
      navigate('/');
    } catch (error) {
      console.error('Error deleting post:', error);
      showToast('Failed to delete post', 'error');
    }
  };

  const handleDeleteComment = async (commentId) => {
    if (!window.confirm('Are you sure you want to delete this comment?')) {
      return;
    }

    try {
      await api.delete(`/api/comments/${commentId}`);

      setComments(prev => prev.filter(comment => comment.id !== commentId));
      setPost(prev => ({
        ...prev,
        commentsCount: prev.commentsCount - 1
      }));

      showToast('Comment deleted successfully', 'success');
    } catch (error) {
      console.error('Error deleting comment:', error);
      showToast('Failed to delete comment', 'error');
    }
  };

  if (isLoading) {
    return <div className="loading-container">Loading post...</div>;
  }

  if (!post) {
    return <div className="error-container">Post not found</div>;
  }

  return (
    <div className="post-detail-page">
      <div className="post-detail-header">
        <Link to="/" className="back-button">
          ← Back
        </Link>
        <h1>Post</h1>
      </div>

      <div className="post-detail-content">
        <div className="post-author">
          <Link to={`/profile/${post.author.username}`} className="author-avatar">
            {post.author.profilePictureUrl ? (
              <img src={post.author.profilePictureUrl || "/placeholder.svg"} alt={post.author.name} />
            ) : (
              <div className="default-avatar">
                {post.author.name.charAt(0)}
              </div>
            )}
          </Link>

          <div className="author-info">
            <Link to={`/profile/${post.author.username}`} className="author-name">
              {post.author.name}
            </Link>
            <span className="author-username">@{post.author.username}</span>
          </div>

          {currentUser && currentUser.id === post.author.id && (
            <div className="post-actions">
              <button 
                className="delete-button"
                onClick={handleDeletePost}
              >
                Delete
              </button>
            </div>
          )}
        </div>

        <div className="post-content">
          <p className="post-text">{post.content}</p>

          {post.mediaUrl && (
            <div className="post-media">
              {post.mediaType === 'IMAGE' ? (
                <img src={post.mediaUrl || "/placeholder.svg"} alt="Post media" />
              ) : post.mediaType === 'VIDEO' ? (
                <video src={post.mediaUrl} controls />
              ) : null}
            </div>
          )}

          <div className="post-meta">
            <span className="post-date">
              {formatDistanceToNow(new Date(post.createdAt), { addSuffix: true })}
            </span>
            {post.type !== 'REGULAR' && (
              <span className="post-type">{post.type}</span>
            )}
          </div>

          <div className="post-stats">
            <div className="stat-item">
              <button 
                className={`like-button ${post.likedByCurrentUser ? 'liked' : ''}`}
                onClick={handleLike}
                disabled={!currentUser}
              >
                {post.likedByCurrentUser ? 'Liked' : 'Like'} • {post.likesCount}
              </button>
            </div>
            <div className="stat-item">
              <span className="comments-count">
                Comments • {post.commentsCount}
              </span>
            </div>
          </div>
        </div>

        {currentUser && (
          <div className="comment-form-container">
            <form onSubmit={handleCommentSubmit} className="comment-form">
              <textarea
                value={commentText}
                onChange={(e) => setCommentText(e.target.value)}
                placeholder="Write a comment..."
                rows="3"
              />
              <button 
                type="submit" 
                className="submit-comment"
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Posting...' : 'Post Comment'}
              </button>
            </form>
          </div>
        )}

        <div className="comments-section">
          <h2>Comments ({comments.length})</h2>

          {comments.length === 0 ? (
            <div className="no-comments">
              <p>No comments yet. Be the first to comment!</p>
            </div>
          ) : (
            <div className="comments-list">
              {comments.map(comment => (
                <div key={comment.id} className="comment-item">
                  <div className="comment-author">
                    <Link to={`/profile/${comment.author.username}`} className="comment-avatar">
                      {comment.author.profilePictureUrl ? (
                        <img src={comment.author.profilePictureUrl || "/placeholder.svg"} alt={comment.author.name} />
                      ) : (
                        <div className="default-avatar">
                          {comment.author.name.charAt(0)}
                        </div>
                      )}
                    </Link>

                    <div className="comment-author-info">
                      <Link to={`/profile/${comment.author.username}`} className="comment-author-name">
                        {comment.author.name}
                      </Link>
                      <span className="comment-date">
                        {formatDistanceToNow(new Date(comment.createdAt), { addSuffix: true })}
                      </span>
                    </div>

                    {currentUser && currentUser.id === comment.author.id && (
                      <button 
                        className="delete-comment"
                        onClick={() => handleDeleteComment(comment.id)}
                      >
                        Delete
                      </button>
                    )}
                  </div>

                  <div className="comment-content">
                    <p>{comment.content}</p>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PostDetailPage;
