"use client"

import { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../../contexts/AuthContext"
import { GoogleLogin } from "@react-oauth/google"
import { useAuth0 } from "@auth0/auth0-react"
import "./AuthPages.css"

const LoginPage = () => {
  const [usernameOrEmail, setUsernameOrEmail] = useState("")
  const [password, setPassword] = useState("")
  const [error, setError] = useState("")
  const [loading, setLoading] = useState(false)
  const [googleLoading, setGoogleLoading] = useState(false)
  const { login, loginWithGoogle } = useAuth()
  const navigate = useNavigate()
  const { loginWithRedirect, isLoading: auth0Loading } = useAuth0()

  const handleSubmit = async (e) => {
    e.preventDefault()
    setError("")

    // Form validation
    if (!usernameOrEmail.trim() || !password) {
      setError("Please fill in all fields")
      return
    }

    try {
      setLoading(true)
      const result = await login({ usernameOrEmail, password })

      if (result.success) {
        navigate("/")
      } else {
        setError(result.error || "Failed to login. Please check your credentials.")
      }
    } catch (err) {
      setError("An unexpected error occurred. Please try again.")
      console.error("Login error:", err)
    } finally {
      setLoading(false)
    }
  }

  const handleGoogleSuccess = async (credentialResponse) => {
    try {
      setGoogleLoading(true)
      setError("")

      console.log("Google login successful, processing credential...")

      // The response object from Google OAuth has a credential property
      // which is the ID token we need to send to our backend
      const idToken = credentialResponse.credential

      if (!idToken) {
        setError("Failed to get credentials from Google")
        return
      }

      console.log("Sending Google token to backend...")
      const result = await loginWithGoogle({ idToken })

      if (result.success) {
        console.log("Google login successful, redirecting...")
        navigate("/")
      } else {
        setError(result.error || "Failed to login with Google")
      }
    } catch (error) {
      console.error("Google login error:", error)
      setError("An error occurred during Google login: " + (error.message || "Unknown error"))
    } finally {
      setGoogleLoading(false)
    }
  }

  const handleGoogleFailure = (error) => {
    console.error("Google login error:", error)
    setError("Google login failed. Please try again.")
  }

  const handleAuth0GoogleLogin = () => {
    loginWithRedirect({
      connection: "google-oauth2",
      appState: {
        returnTo: "/",
      },
    })
  }

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h2>Welcome Back</h2>
          <p>Sign in to your SkillUp account</p>
        </div>

        {error && <div className="auth-error">{error}</div>}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-control">
            <label htmlFor="usernameOrEmail">Username or Email</label>
            <input
              type="text"
              id="usernameOrEmail"
              value={usernameOrEmail}
              onChange={(e) => setUsernameOrEmail(e.target.value)}
              placeholder="Enter your username or email"
              disabled={loading || googleLoading || auth0Loading}
            />
          </div>

          <div className="form-control">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              disabled={loading || googleLoading || auth0Loading}
            />
          </div>

          <button type="submit" className="btn btn-primary w-full" disabled={loading || googleLoading || auth0Loading}>
            {loading ? "Signing in..." : "Sign In"}
          </button>

          <div className="auth-divider">
            <span>OR</span>
          </div>

          {/* Google Cloud via Auth0 Login Button */}
          <button
            type="button"
            className="btn btn-google w-full"
            onClick={handleAuth0GoogleLogin}
            disabled={loading || googleLoading || auth0Loading}
            style={{
              marginTop: "10px",
              backgroundColor: "#ffffff",
              color: "#757575",
              border: "1px solid #dadce0",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              gap: "8px",
              padding: "10px",
              borderRadius: "4px",
            }}
          >
            <img src="/google.png" alt="Google logo" style={{ width: "18px", height: "18px" }} />
            {auth0Loading ? "Connecting..." : "Sign in with Google Cloud"}
          </button>

          {/* Direct Google Login Button */}
          <div className="google-login-container" style={{ marginTop: "10px" }}>
            {googleLoading ? (
              <div className="google-loading">Signing in with Google...</div>
            ) : (
              <GoogleLogin
                onSuccess={handleGoogleSuccess}
                onError={handleGoogleFailure}
                useOneTap
                text="signin_with"
                theme="outline"
                shape="rectangular"
                size="large"
                width="100%"
                logo_alignment="center"
              />
            )}
          </div>
        </form>

        <div className="auth-footer">
          <p>
            Don't have an account? <Link to="/register">Sign Up</Link>
          </p>
        </div>
      </div>
    </div>
  )
}

export default LoginPage
