"use client"

import { useState, useEffect } from "react"
import { useParams, Link } from "react-router-dom"
import { userAPI, postAPI } from "../../utils/api"
import { useAuth } from "../../contexts/AuthContext"
import PostItem from "../../components/posts/PostItem"
import Loading from "../../components/common/Loading"
import "./ProfilePage.css"

const ProfilePage = () => {
  const { username } = useParams()
  const { currentUser } = useAuth()
  const [user, setUser] = useState(null)
  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [isFollowing, setIsFollowing] = useState(false)

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        setLoading(true)
        const response = await userAPI.getUserByUsername(username)
        setUser(response.data.data)
        setIsFollowing(response.data.data.isFollowing)

        // Fetch user's posts
        const postsResponse = await postAPI.getPostsByUserId(response.data.data.id)
        setPosts(postsResponse.data.data.content)
      } catch (error) {
        console.error("Error fetching user data:", error)
        setError("Failed to load user profile")
      } finally {
        setLoading(false)
      }
    }

    fetchUserData()
  }, [username])

  const handleFollow = async () => {
    try {
      if (isFollowing) {
        await userAPI.unfollowUser(user.id)
      } else {
        await userAPI.followUser(user.id)
      }
      setIsFollowing(!isFollowing)
      setUser((prevUser) => ({
        ...prevUser,
        followersCount: isFollowing ? prevUser.followersCount - 1 : prevUser.followersCount + 1,
      }))
    } catch (error) {
      console.error("Error following/unfollowing user:", error)
    }
  }

  const getImageUrl = (url) => {
    if (!url) return "/placeholder.svg" // Fallback if image is not available
    if (url.startsWith("http")) return url
    const baseUrl = process.env.REACT_APP_API_URL || "http://localhost:8080"
    return `${baseUrl}${url}`
  }

  if (loading) return <Loading />

  if (error) return <div className="error-message">{error}</div>

  if (!user) return <div className="not-found">User not found</div>

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-cover">
          <img src={getImageUrl(user.coverImage)} alt="Cover" className="cover-image" />
        </div>
        <div className="profile-info">
          <div className="profile-avatar">
            <img src={getImageUrl(user.avatarUrl)} alt={user.username} />
          </div>
          <div className="profile-details">
            <h1>{user.name || user.username}</h1>
            <p className="username">@{user.username}</p>
            {user.bio && <p className="bio">{user.bio}</p>}
            <div className="profile-stats">
              <div className="stat">
                <span className="count">{posts.length}</span> Posts
              </div>
              <Link to={`/profile/${username}/followers`} className="stat">
                <span className="count">{user.followersCount}</span> Followers
              </Link>
              <Link to={`/profile/${username}/following`} className="stat">
                <span className="count">{user.followingCount}</span> Following
              </Link>
            </div>
            <div style={{marginTop:"10px"}}></div>
            {currentUser && currentUser.id === user.id ? (
              <Link to="/profile/edit" className="edit-profile-btn" >
                Edit Profile
              </Link>
            ) : (
              <button onClick={handleFollow} className={`follow-btn ${isFollowing ? "following" : ""}`}>
                {isFollowing ? "Following" : "Follow"}
              </button>
            )}
          </div>
        </div>
      </div>

      <div className="profile-content">
        <h2>Posts</h2>
        {posts.length === 0 ? (
          <p className="no-posts">No posts yet</p>
        ) : (
          <div className="posts-grid">
            {posts.map((post) => (
              <PostItem key={post.id} post={post} />
            ))}
          </div>
        )}
      </div>
    </div>
  )
}

export default ProfilePage
