"use client"

import { useState, useEffect } from "react"
import { useParams, Link, useNavigate } from "react-router-dom"
import { format } from "date-fns"
import { api } from "../../utils/api"
import { useAuth } from "../../contexts/AuthContext"
import { useToast } from "../../contexts/ToastContext"
import Layout from "../../components/layout/Layout"
import "./PostDetailPage.css"

const PostDetailPage = () => {
  const { postId } = useParams()
  const { currentUser } = useAuth()
  const { showToast } = useToast()
  const navigate = useNavigate()

  const [post, setPost] = useState(null)
  const [comments, setComments] = useState([])
  const [commentText, setCommentText] = useState("")
  const [loading, setLoading] = useState(true)
  const [commentLoading, setCommentLoading] = useState(false)
  const [liked, setLiked] = useState(false)
  const [likeCount, setLikeCount] = useState(0)

  useEffect(() => {
    const fetchPostDetails = async () => {
      try {
        setLoading(true)

        // Fetch post details
        const postResponse = await api.get(`/posts/${postId}`)
        setPost(postResponse.data)
        setLikeCount(postResponse.data.likeCount || 0)

        // Fetch comments
        const commentsResponse = await api.get(`/comments/post/${postId}`)
        setComments(commentsResponse.data)

        // Check if current user has liked this post
        if (currentUser) {
          try {
            const likeResponse = await api.get(`/likes/post/${postId}/user/${currentUser.id}`)
            setLiked(likeResponse.data)
          } catch (error) {
            // If 404, user hasn't liked the post
            if (error.response && error.response.status === 404) {
              setLiked(false)
            } else {
              console.error("Error checking like status:", error)
            }
          }
        }

        setLoading(false)
      } catch (error) {
        console.error("Error fetching post details:", error)
        showToast("Failed to load post", "error")
        setLoading(false)
      }
    }

    fetchPostDetails()
  }, [postId, currentUser, showToast])

  const handleLike = async () => {
    if (!currentUser) {
      showToast("Please login to like posts", "warning")
      return
    }

    try {
      if (liked) {
        await api.delete(`/likes/post/${postId}/user/${currentUser.id}`)
        setLiked(false)
        setLikeCount((prev) => prev - 1)
      } else {
        await api.post(`/likes/post/${postId}/user/${currentUser.id}`)
        setLiked(true)
        setLikeCount((prev) => prev + 1)
      }
    } catch (error) {
      console.error("Error updating like status:", error)
      showToast("Failed to update like status", "error")
    }
  }

  const handleCommentSubmit = async (e) => {
    e.preventDefault()

    if (!currentUser) {
      showToast("Please login to comment", "warning")
      return
    }

    if (!commentText.trim()) {
      showToast("Comment cannot be empty", "warning")
      return
    }

    setCommentLoading(true)

    try {
      const response = await api.post("/comments", {
        postId: Number.parseInt(postId),
        content: commentText,
      })

      // Add the new comment to the list
      setComments((prev) => [response.data, ...prev])
      setCommentText("")
      showToast("Comment added successfully", "success")
    } catch (error) {
      console.error("Error adding comment:", error)
      showToast(error.response?.data?.message || "Failed to add comment", "error")
    } finally {
      setCommentLoading(false)
    }
  }

  const handleDeletePost = async () => {
    if (!window.confirm("Are you sure you want to delete this post?")) {
      return
    }

    try {
      await api.delete(`/posts/${postId}`)
      showToast("Post deleted successfully", "success")
      navigate("/")
    } catch (error) {
      console.error("Error deleting post:", error)
      showToast("Failed to delete post", "error")
    }
  }

  if (loading) {
    return (
      <Layout>
        <div className="post-detail-container">
          <div className="post-loading">Loading post...</div>
        </div>
      </Layout>
    )
  }

  if (!post) {
    return (
      <Layout>
        <div className="post-detail-container">
          <div className="post-error">Post not found</div>
        </div>
      </Layout>
    )
  }

  return (
    <Layout>
      <div className="post-detail-container">
        <div className="post-detail-header">
          <div className="post-detail-user">
            <Link to={`/profile/${post.user.username}`}>
              <img
                src={post.user.profileImage || "/default-avatar.png"}
                alt={`${post.user.username}'s avatar`}
                className="post-user-avatar"
              />
            </Link>
            <div className="post-user-info">
              <Link to={`/profile/${post.user.username}`} className="post-username">
                {post.user.username}
              </Link>
              <span className="post-date">{format(new Date(post.createdAt), "MMM d, yyyy")}</span>
            </div>
          </div>

          {currentUser && currentUser.id === post.user.id && (
            <div className="post-actions">
              <button onClick={handleDeletePost} className="delete-post-button">
                Delete Post
              </button>
            </div>
          )}
        </div>

        <div className="post-detail-content">
          <div className="post-type-badge">{post.postType}</div>
          <h1 className="post-title">{post.title}</h1>
          <p className="post-content">{post.content}</p>

          {post.media && (
            <div className="post-media">
              {post.media.mediaType.startsWith("IMAGE") ? (
                <img src={post.media.url || "/placeholder.svg"} alt="Post media" />
              ) : post.media.mediaType.startsWith("VIDEO") ? (
                <video controls src={post.media.url} />
              ) : null}
            </div>
          )}

          <div className="post-stats">
            <button className={`like-button ${liked ? "liked" : ""}`} onClick={handleLike}>
              {liked ? "Liked" : "Like"} ({likeCount})
            </button>
            <span className="comment-count">{comments.length} Comments</span>
          </div>
        </div>

        <div className="post-comments-section">
          <h2>Comments</h2>

          {currentUser && (
            <form onSubmit={handleCommentSubmit} className="comment-form">
              <textarea
                value={commentText}
                onChange={(e) => setCommentText(e.target.value)}
                placeholder="Add a comment..."
                rows="3"
                required
              />
              <button type="submit" disabled={commentLoading} className="submit-comment-button">
                {commentLoading ? "Posting..." : "Post Comment"}
              </button>
            </form>
          )}

          {!currentUser && (
            <div className="login-to-comment">
              <Link to="/login">Login to comment</Link>
            </div>
          )}

          {comments.length === 0 ? (
            <div className="no-comments">
              <p>No comments yet. Be the first to comment!</p>
            </div>
          ) : (
            <div className="comments-list">
              {comments.map((comment) => (
                <div key={comment.id} className="comment-item">
                  <div className="comment-header">
                    <Link to={`/profile/${comment.user.username}`} className="comment-user-info">
                      <img
                        src={comment.user.profileImage || "/default-avatar.png"}
                        alt={`${comment.user.username}'s avatar`}
                        className="comment-user-avatar"
                      />
                      <span className="comment-username">{comment.user.username}</span>
                    </Link>
                    <span className="comment-date">{format(new Date(comment.createdAt), "MMM d, yyyy")}</span>
                  </div>
                  <p className="comment-content">{comment.content}</p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </Layout>
  )
}

export default PostDetailPage
