import React, { useState, useEffect } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { api } from '../../utils/api';
import { useAuth } from '../../contexts/AuthContext';
import { useToast } from '../../contexts/ToastContext';
import PostItem from '../../components/posts/PostItem';
import './ProfilePages.css';

const ProfilePage = () => {
  const { username } = useParams();
  const { currentUser } = useAuth();
  const { showToast } = useToast();
  const navigate = useNavigate();

  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [learningPlans, setLearningPlans] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('posts');
  const [stats, setStats] = useState({
    postsCount: 0,
    followersCount: 0,
    followingCount: 0
  });
  const [isFollowing, setIsFollowing] = useState(false);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        setIsLoading(true);

        // Get user details
        const userResponse = await api.get(`/api/users/username/${username}`);
        setUser(userResponse.data);

        // Check if current user is following this user
        if (currentUser && currentUser.id !== userResponse.data.id) {
          const followStatusResponse = await api.get(`/api/users/${userResponse.data.id}/follow-status`);
          setIsFollowing(followStatusResponse.data.isFollowing);
        }

        // Get user stats
        const statsResponse = await api.get(`/api/users/${userResponse.data.id}/stats`);
        setStats(statsResponse.data);

        // Get user posts
        const postsResponse = await api.get(`/api/users/${userResponse.data.id}/posts`);
        setPosts(postsResponse.data);

        // Get user learning plans
        const learningPlansResponse = await api.get(`/api/users/${userResponse.data.id}/learning-plans`);
        setLearningPlans(learningPlansResponse.data);
      } catch (error) {
        console.error('Error fetching user profile:', error);
        showToast('Failed to load user profile', 'error');
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserProfile();
  }, [username, currentUser, showToast]);

  const handleFollow = async () => {
    try {
      await api.post(`/api/users/${user.id}/follow`);
      setIsFollowing(true);
      setStats(prev => ({
        ...prev,
        followersCount: prev.followersCount + 1
      }));
      showToast(`You are now following ${user.name}`, 'success');
    } catch (error) {
      console.error('Error following user:', error);
      showToast('Failed to follow user', 'error');
    }
  };

  const handleUnfollow = async () => {
    try {
      await api.delete(`/api/users/${user.id}/unfollow`);
      setIsFollowing(false);
      setStats(prev => ({
        ...prev,
        followersCount: prev.followersCount - 1
      }));
      showToast(`You have unfollowed ${user.name}`, 'success');
    } catch (error) {
      console.error('Error unfollowing user:', error);
      showToast('Failed to unfollow user', 'error');
    }
  };

  if (isLoading) {
    return <div className="loading-container">Loading profile...</div>;
  }

  if (!user) {
    return <div className="error-container">User not found</div>;
  }

  const isCurrentUser = currentUser && currentUser.id === user.id;

  return (
    <div className="profile-page">
      <div className="profile-header">
        <div className="profile-cover">
          {user.coverPictureUrl && (
            <img src={user.coverPictureUrl || "/placeholder.svg"} alt="Cover" className="cover-image" />
          )}
        </div>

        <div className="profile-info">
          <div className="profile-avatar">
            {user.profilePictureUrl ? (
              <img src={user.profilePictureUrl || "/placeholder.svg"} alt={user.name} />
            ) : (
              <div className="default-avatar">
                {user.name.charAt(0)}
              </div>
            )}
          </div>

          <div className="profile-details">
            <h1 className="profile-name">{user.name}</h1>
            <p className="profile-username">@{user.username}</p>

            {user.bio && <p className="profile-bio">{user.bio}</p>}

            <div className="profile-stats">
              <div className="stat-item">
                <span className="stat-value">{stats.postsCount}</span>
                <span className="stat-label">Posts</span>
              </div>

              <Link to={`/profile/${username}/followers`} className="stat-item">
                <span className="stat-value">{stats.followersCount}</span>
                <span className="stat-label">Followers</span>
              </Link>

              <Link to={`/profile/${username}/following`} className="stat-item">
                <span className="stat-value">{stats.followingCount}</span>
                <span className="stat-label">Following</span>
              </Link>
            </div>

            {user.skills && user.skills.length > 0 && (
              <div className="profile-skills">
                <h3>Skills</h3>
                <div className="skills-list">
                  {user.skills.map((skill, index) => (
                    <span key={index} className="skill-tag">{skill}</span>
                  ))}
                </div>
              </div>
            )}

            {user.interests && user.interests.length > 0 && (
              <div className="profile-interests">
                <h3>Interests</h3>
                <div className="interests-list">
                  {user.interests.map((interest, index) => (
                    <span key={index} className="interest-tag">{interest}</span>
                  ))}
                </div>
              </div>
            )}
          </div>

          <div className="profile-actions">
            {isCurrentUser ? (
              <button 
                className="edit-profile-button"
                onClick={() => navigate('/profile/edit')}
              >
                Edit Profile
              </button>
            ) : (
              <button 
                className={isFollowing ? "unfollow-button" : "follow-button"}
                onClick={isFollowing ? handleUnfollow : handleFollow}
              >
                {isFollowing ? 'Unfollow' : 'Follow'}
              </button>
            )}
          </div>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-tabs">
          <button 
            className={`tab-button ${activeTab === 'posts' ? 'active' : ''}`}
            onClick={() => setActiveTab('posts')}
          >
            Posts
          </button>
          <button 
            className={`tab-button ${activeTab === 'learning-plans' ? 'active' : ''}`}
            onClick={() => setActiveTab('learning-plans')}
          >
            Learning Plans
          </button>
        </div>

        <div className="tab-content">
          {activeTab === 'posts' && (
            <div className="posts-tab">
              {posts.length === 0 ? (
                <div className="no-content">
                  <p>No posts yet</p>
                  {isCurrentUser && (
                    <button 
                      className="create-button"
                      onClick={() => navigate('/posts/create')}
                    >
                      Create a Post
                    </button>
                  )}
                </div>
              ) : (
                <div className="posts-list">
                  {posts.map(post => (
                    <PostItem key={post.id} post={post} />
                  ))}
                </div>
              )}
            </div>
          )}

          {activeTab === 'learning-plans' && (
            <div className="learning-plans-tab">
              {learningPlans.length === 0 ? (
                <div className="no-content">
                  <p>No learning plans yet</p>
                  {isCurrentUser && (
                    <button 
                      className="create-button"
                      onClick={() => navigate('/learning-plans/create')}
                    >
                      Create a Learning Plan
                    </button>
                  )}
                </div>
              ) : (
                <div className="learning-plans-list">
                  {learningPlans.map(plan => (
                    <div key={plan.id} className="learning-plan-card">
                      <h3 className="plan-title">
                        <Link to={`/learning-plans/${plan.id}`}>{plan.title}</Link>
                      </h3>
                      <p className="plan-description">{plan.description}</p>
                      <div className="plan-topics">
                        <span className="topics-count">{plan.topicsCount} topics</span>
                      </div>
                      <div className="plan-meta">
                        <span className="plan-date">Created on {new Date(plan.createdAt).toLocaleDateString()}</span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
