import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { api } from '../../utils/api';
import { useAuth } from '../../contexts/AuthContext';
import { useToast } from '../../contexts/ToastContext';
import './ProfilePages.css';

const FollowingPage = () => {
  const { username } = useParams();
  const { currentUser } = useAuth();
  const { showToast } = useToast();

  const [following, setFollowing] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUserAndFollowing = async () => {
      try {
        // First get the user details
        const userResponse = await api.get(`/api/users/username/${username}`);
        setUser(userResponse.data);

        // Then get the following
        const followingResponse = await api.get(`/api/users/${userResponse.data.id}/following`);
        setFollowing(followingResponse.data);
      } catch (error) {
        console.error('Error fetching following:', error);
        showToast('Failed to load following', 'error');
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserAndFollowing();
  }, [username, showToast]);

  const handleFollow = async (userId) => {
    try {
      await api.post(`/api/users/${userId}/follow`);

      // Update the following list
      setFollowing(prevFollowing => 
        prevFollowing.map(followedUser => 
          followedUser.id === userId 
            ? { ...followedUser, isFollowedByCurrentUser: true }
            : followedUser
        )
      );

      showToast('User followed successfully', 'success');
    } catch (error) {
      console.error('Error following user:', error);
      showToast('Failed to follow user', 'error');
    }
  };

  const handleUnfollow = async (userId) => {
    try {
      await api.delete(`/api/users/${userId}/unfollow`);

      // Update the following list
      setFollowing(prevFollowing => 
        prevFollowing.map(followedUser => 
          followedUser.id === userId 
            ? { ...followedUser, isFollowedByCurrentUser: false }
            : followedUser
        )
      );

      showToast('User unfollowed successfully', 'success');
    } catch (error) {
      console.error('Error unfollowing user:', error);
      showToast('Failed to unfollow user', 'error');
    }
  };

  if (isLoading) {
    return <div className="loading-container">Loading following...</div>;
  }

  if (!user) {
    return <div className="error-container">User not found</div>;
  }

  return (
    <div className="following-page">
      <div className="following-header">
        <h1>People {user.name} Follows</h1>
        <Link to={`/profile/${username}`} className="back-to-profile">
          Back to Profile
        </Link>
      </div>

      {following.length === 0 ? (
        <div className="no-following">
          <p>Not following anyone yet</p>
        </div>
      ) : (
        <div className="following-list">
          {following.map(followedUser => (
            <div key={followedUser.id} className="following-item">
              <div className="following-info">
                <Link to={`/profile/${followedUser.username}`} className="following-avatar">
                  {followedUser.profilePictureUrl ? (
                    <img src={followedUser.profilePictureUrl || "/placeholder.svg"} alt={followedUser.name} />
                  ) : (
                    <div className="default-avatar">
                      {followedUser.name.charAt(0)}
                    </div>
                  )}
                </Link>

                <div className="following-details">
                  <Link to={`/profile/${followedUser.username}`} className="following-name">
                    {followedUser.name}
                  </Link>
                  <span className="following-username">@{followedUser.username}</span>
                  {followedUser.bio && <p className="following-bio">{followedUser.bio}</p>}
                </div>
              </div>

              {currentUser && currentUser.id !== followedUser.id && (
                <div className="follow-action">
                  {followedUser.isFollowedByCurrentUser ? (
                    <button 
                      className="unfollow-button"
                      onClick={() => handleUnfollow(followedUser.id)}
                    >
                      Unfollow
                    </button>
                  ) : (
                    <button 
                      className="follow-button"
                      onClick={() => handleFollow(followedUser.id)}
                    >
                      Follow
                    </button>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FollowingPage;
