import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { api } from '../../utils/api';
import { useAuth } from '../../contexts/AuthContext';
import { useToast } from '../../contexts/ToastContext';
import './ProfilePages.css';

const FollowersPage = () => {
  const { username } = useParams();
  const { currentUser } = useAuth();
  const { showToast } = useToast();

  const [followers, setFollowers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUserAndFollowers = async () => {
      try {
        // First get the user details
        const userResponse = await api.get(`/api/users/username/${username}`);
        setUser(userResponse.data);

        // Then get the followers
        const followersResponse = await api.get(`/api/users/${userResponse.data.id}/followers`);
        setFollowers(followersResponse.data);
      } catch (error) {
        console.error('Error fetching followers:', error);
        showToast('Failed to load followers', 'error');
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserAndFollowers();
  }, [username, showToast]);

  const handleFollow = async (userId) => {
    try {
      await api.post(`/api/users/${userId}/follow`);

      // Update the followers list
      setFollowers(prevFollowers => 
        prevFollowers.map(follower => 
          follower.id === userId 
            ? { ...follower, isFollowedByCurrentUser: true }
            : follower
        )
      );

      showToast('User followed successfully', 'success');
    } catch (error) {
      console.error('Error following user:', error);
      showToast('Failed to follow user', 'error');
    }
  };

  const handleUnfollow = async (userId) => {
    try {
      await api.delete(`/api/users/${userId}/unfollow`);

      // Update the followers list
      setFollowers(prevFollowers => 
        prevFollowers.map(follower => 
          follower.id === userId 
            ? { ...follower, isFollowedByCurrentUser: false }
            : follower
        )
      );

      showToast('User unfollowed successfully', 'success');
    } catch (error) {
      console.error('Error unfollowing user:', error);
      showToast('Failed to unfollow user', 'error');
    }
  };

  if (isLoading) {
    return <div className="loading-container">Loading followers...</div>;
  }

  if (!user) {
    return <div className="error-container">User not found</div>;
  }

  return (
    <div className="followers-page">
      <div className="followers-header">
        <h1>{user.name}'s Followers</h1>
        <Link to={`/profile/${username}`} className="back-to-profile">
          Back to Profile
        </Link>
      </div>

      {followers.length === 0 ? (
        <div className="no-followers">
          <p>No followers yet</p>
        </div>
      ) : (
        <div className="followers-list">
          {followers.map(follower => (
            <div key={follower.id} className="follower-item">
              <div className="follower-info">
                <Link to={`/profile/${follower.username}`} className="follower-avatar">
                  {follower.profilePictureUrl ? (
                    <img src={follower.profilePictureUrl || "/placeholder.svg"} alt={follower.name} />
                  ) : (
                    <div className="default-avatar">
                      {follower.name.charAt(0)}
                    </div>
                  )}
                </Link>

                <div className="follower-details">
                  <Link to={`/profile/${follower.username}`} className="follower-name">
                    {follower.name}
                  </Link>
                  <span className="follower-username">@{follower.username}</span>
                  {follower.bio && <p className="follower-bio">{follower.bio}</p>}
                </div>
              </div>

              {currentUser && currentUser.id !== follower.id && (
                <div className="follow-action">
                  {follower.isFollowedByCurrentUser ? (
                    <button 
                      className="unfollow-button"
                      onClick={() => handleUnfollow(follower.id)}
                    >
                      Unfollow
                    </button>
                  ) : (
                    <button 
                      className="follow-button"
                      onClick={() => handleFollow(follower.id)}
                    >
                      Follow
                    </button>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FollowersPage;
