import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useToast } from '../../contexts/ToastContext';
import { api } from '../../utils/api';
import './ProfilePages.css';

const EditProfilePage = () => {
  const { currentUser, setCurrentUser } = useAuth();
  const { showToast } = useToast();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: '',
    username: '',
    email: '',
    bio: '',
    profilePicture: null,
    skills: '',
    interests: ''
  });

  const [previewUrl, setPreviewUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (currentUser) {
      setFormData({
        name: currentUser.name || '',
        username: currentUser.username || '',
        email: currentUser.email || '',
        bio: currentUser.bio || '',
        profilePicture: null,
        skills: currentUser.skills?.join(', ') || '',
        interests: currentUser.interests?.join(', ') || ''
      });

      if (currentUser.profilePictureUrl) {
        setPreviewUrl(currentUser.profilePictureUrl);
      }
    }
  }, [currentUser]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        profilePicture: file
      }));

      // Create preview URL
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      // Create form data for file upload
      const formDataToSend = new FormData();
      formDataToSend.append('name', formData.name);
      formDataToSend.append('username', formData.username);
      formDataToSend.append('email', formData.email);
      formDataToSend.append('bio', formData.bio);

      // Convert comma-separated strings to arrays
      const skills = formData.skills.split(',').map(skill => skill.trim()).filter(Boolean);
      const interests = formData.interests.split(',').map(interest => interest.trim()).filter(Boolean);

      formDataToSend.append('skills', JSON.stringify(skills));
      formDataToSend.append('interests', JSON.stringify(interests));

      if (formData.profilePicture) {
        formDataToSend.append('profilePicture', formData.profilePicture);
      }

      const response = await api.put(`/api/users/${currentUser.id}`, formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setCurrentUser(response.data);
      showToast('Profile updated successfully!', 'success');
      navigate(`/profile/${currentUser.username}`);
    } catch (error) {
      console.error('Error updating profile:', error);
      showToast('Failed to update profile. Please try again.', 'error');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="edit-profile-container">
      <h1>Edit Profile</h1>

      <form onSubmit={handleSubmit} className="edit-profile-form">
        <div className="profile-picture-section">
          <div className="profile-picture-preview">
            {previewUrl ? (
              <img src={previewUrl || "/placeholder.svg"} alt="Profile Preview" />
            ) : (
              <div className="no-profile-picture">
                <span>{currentUser?.name?.charAt(0) || 'U'}</span>
              </div>
            )}
          </div>

          <div className="profile-picture-upload">
            <label htmlFor="profilePicture" className="upload-button">
              Change Profile Picture
            </label>
            <input
              type="file"
              id="profilePicture"
              name="profilePicture"
              accept="image/*"
              onChange={handleFileChange}
              className="file-input"
            />
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            name="username"
            value={formData.username}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="bio">Bio</label>
          <textarea
            id="bio"
            name="bio"
            value={formData.bio}
            onChange={handleChange}
            rows="4"
          />
        </div>

        <div className="form-group">
          <label htmlFor="skills">Skills (comma-separated)</label>
          <input
            type="text"
            id="skills"
            name="skills"
            value={formData.skills}
            onChange={handleChange}
            placeholder="e.g. JavaScript, React, Node.js"
          />
        </div>

        <div className="form-group">
          <label htmlFor="interests">Interests (comma-separated)</label>
          <input
            type="text"
            id="interests"
            name="interests"
            value={formData.interests}
            onChange={handleChange}
            placeholder="e.g. Web Development, UI/UX Design, Machine Learning"
          />
        </div>

        <div className="form-actions">
          <button 
            type="button" 
            className="cancel-button"
            onClick={() => navigate(`/profile/${currentUser.username}`)}
          >
            Cancel
          </button>
          <button 
            type="submit" 
            className="save-button"
            disabled={isLoading}
          >
            {isLoading ? 'Saving...' : 'Save Changes'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditProfilePage;
