import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useToast } from '../../contexts/ToastContext';
import { api } from '../../utils/api';
import './LearningPlanPages.css';

const CreateLearningPlanPage = () => {
  const { currentUser } = useAuth();
  const { showToast } = useToast();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    isPublic: true
  });

  const [topics, setTopics] = useState([
    { title: '', description: '', resources: '', order: 0 }
  ]);

  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleTopicChange = (index, e) => {
    const { name, value } = e.target;
    const updatedTopics = [...topics];
    updatedTopics[index] = {
      ...updatedTopics[index],
      [name]: value
    };
    setTopics(updatedTopics);
  };

  const addTopic = () => {
    setTopics(prev => [
      ...prev,
      { title: '', description: '', resources: '', order: prev.length }
    ]);
  };

  const removeTopic = (index) => {
    if (topics.length === 1) {
      showToast('Learning plan must have at least one topic', 'error');
      return;
    }

    const updatedTopics = topics.filter((_, i) => i !== index);
    // Update order for remaining topics
    updatedTopics.forEach((topic, i) => {
      topic.order = i;
    });

    setTopics(updatedTopics);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form
    if (!formData.title.trim()) {
      showToast('Please enter a title for your learning plan', 'error');
      return;
    }

    // Validate topics
    const invalidTopics = topics.filter(topic => !topic.title.trim());
    if (invalidTopics.length > 0) {
      showToast('All topics must have a title', 'error');
      return;
    }

    setIsLoading(true);

    try {
      // Create learning plan
      const learningPlanResponse = await api.post('/api/learning-plans', {
        title: formData.title,
        description: formData.description,
        isPublic: formData.isPublic
      });

      const learningPlanId = learningPlanResponse.data.id;

      // Create topics
      const topicPromises = topics.map(topic => 
        api.post(`/api/learning-plans/${learningPlanId}/topics`, {
          title: topic.title,
          description: topic.description,
          resources: topic.resources,
          order: topic.order
        })
      );

      await Promise.all(topicPromises);

      showToast('Learning plan created successfully!', 'success');
      navigate(`/learning-plans/${learningPlanId}`);
    } catch (error) {
      console.error('Error creating learning plan:', error);
      showToast('Failed to create learning plan. Please try again.', 'error');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="create-learning-plan-container">
      <h1>Create Learning Plan</h1>

      <form onSubmit={handleSubmit} className="learning-plan-form">
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="e.g., Web Development Fundamentals"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="4"
            placeholder="Describe what this learning plan is about..."
          />
        </div>

        <div className="form-group checkbox-group">
          <label>
            <input
              type="checkbox"
              name="isPublic"
              checked={formData.isPublic}
              onChange={handleChange}
            />
            Make this learning plan public
          </label>
        </div>

        <div className="topics-section">
          <h2>Topics</h2>

          {topics.map((topic, index) => (
            <div key={index} className="topic-item">
              <div className="topic-header">
                <h3>Topic {index + 1}</h3>
                <button 
                  type="button" 
                  className="remove-topic-button"
                  onClick={() => removeTopic(index)}
                >
                  Remove
                </button>
              </div>

              <div className="form-group">
                <label htmlFor={`topic-title-${index}`}>Title</label>
                <input
                  type="text"
                  id={`topic-title-${index}`}
                  name="title"
                  value={topic.title}
                  onChange={(e) => handleTopicChange(index, e)}
                  placeholder="e.g., HTML Basics"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor={`topic-description-${index}`}>Description</label>
                <textarea
                  id={`topic-description-${index}`}
                  name="description"
                  value={topic.description}
                  onChange={(e) => handleTopicChange(index, e)}
                  rows="3"
                  placeholder="Describe what this topic covers..."
                />
              </div>

              <div className="form-group">
                <label htmlFor={`topic-resources-${index}`}>Resources</label>
                <textarea
                  id={`topic-resources-${index}`}
                  name="resources"
                  value={topic.resources}
                  onChange={(e) => handleTopicChange(index, e)}
                  rows="3"
                  placeholder="Add links to helpful resources, separated by new lines..."
                />
              </div>
            </div>
          ))}

          <button 
            type="button" 
            className="add-topic-button"
            onClick={addTopic}
          >
            + Add Topic
          </button>
        </div>

        <div className="form-actions">
          <button 
            type="button" 
            className="cancel-button"
            onClick={() => navigate('/')}
          >
            Cancel
          </button>
          <button 
            type="submit" 
            className="submit-button"
            disabled={isLoading}
          >
            {isLoading ? 'Creating...' : 'Create Learning Plan'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateLearningPlanPage;
