"use client"

import { useState } from "react"
import { useNavigate } from "react-router-dom"
import axios from "axios"
import { useAuth } from "../../contexts/AuthContext"
import { useToast } from "../../contexts/ToastContext"
import Layout from "../../components/layout/Layout"
import "./StoryPages.css"

const CreateStoryPage = () => {
  const { currentUser } = useAuth()
  const { showToast } = useToast()
  const navigate = useNavigate()

  const [title, setTitle] = useState("")
  const [content, setContent] = useState("")
  const [mediaFile, setMediaFile] = useState(null)
  const [mediaPreview, setMediaPreview] = useState("")
  const [loading, setLoading] = useState(false)

  const handleTitleChange = (e) => {
    setTitle(e.target.value)
  }

  const handleContentChange = (e) => {
    setContent(e.target.value)
  }

  const handleFileChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      console.log("Selected file:", file.name, file.type, file.size)

      // Check if file is image or video
      if (!file.type.match("image.*") && !file.type.match("video.*")) {
        showToast("Please upload an image or video file", "error")
        return
      }

      setMediaFile(file)

      // Create media preview
      const reader = new FileReader()
      reader.onloadend = () => {
        setMediaPreview(reader.result)
      }
      reader.readAsDataURL(file)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    console.log("Starting story submission...")

    if (!currentUser) {
      showToast("Please login to create a story", "error")
      navigate("/login")
      return
    }

    if (!mediaFile) {
      showToast("Please upload an image or video for your story", "warning")
      return
    }

    setLoading(true)
    console.log("Form data:", { title, content, mediaFile: mediaFile ? mediaFile.name : "none" })

    // Create FormData object
    const formData = new FormData()

    // Create a story request object
    const storyRequest = {
      title: title,
      content: content,
    }

    // Add as JSON string
    formData.append("story", new Blob([JSON.stringify(storyRequest)], { type: "application/json" }))

    // Add the media file
    formData.append("media", mediaFile)

    // Log what we're submitting for debugging
    console.log("Submitting story with:", {
      title,
      content,
      mediaFile: mediaFile
        ? {
            name: mediaFile.name,
            type: mediaFile.type,
            size: mediaFile.size,
          }
        : null,
    })

    // Get the token from localStorage
    const token = localStorage.getItem("token")
    console.log("Using token:", token ? "Token exists" : "No token found")

    try {
      const baseUrl = process.env.REACT_APP_API_URL || "http://localhost:8080"
      console.log("API URL:", baseUrl)

      // Make a direct axios call to the backend
      const response = await axios.post(`${baseUrl}/api/stories`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: token ? `Bearer ${token}` : "",
        },
      })

      console.log("Story created successfully:", response.data)
      showToast("Story created successfully!", "success")
      navigate("/stories")
    } catch (error) {
      console.error("Error creating story:", error)
      console.error("Error details:", error.response?.data || "No response data")
      showToast(error.response?.data?.message || "Failed to create story. Check console for details.", "error")
    } finally {
      setLoading(false)
    }
  }

  return (
    <Layout>
      <div className="create-story-container">
        <h1>Create a New Story</h1>

        <form onSubmit={handleSubmit} className="create-story-form">
          <div className="form-group">
            <label htmlFor="title">Title (Optional)</label>
            <input
              type="text"
              id="title"
              value={title}
              onChange={handleTitleChange}
              placeholder="Give your story a title"
            />
          </div>

          <div className="form-group">
            <label htmlFor="content">Content (Optional)</label>
            <textarea
              id="content"
              value={content}
              onChange={handleContentChange}
              placeholder="Add a description or content for your story"
              rows="3"
            />
          </div>

          <div className="form-group">
            <label htmlFor="media">Upload Media (Required)</label>
            <input type="file" id="media" onChange={handleFileChange} accept="image/*,video/*" required />

            {mediaPreview && (
              <div className="media-preview">
                {mediaFile?.type.startsWith("image/") ? (
                  <img src={mediaPreview || "/placeholder.svg"} alt="Story preview" />
                ) : mediaFile?.type.startsWith("video/") ? (
                  <video controls src={mediaPreview} />
                ) : null}
              </div>
            )}
          </div>

          <div className="form-actions">
            <button type="button" onClick={() => navigate("/stories")} className="cancel-button" disabled={loading}>
              Cancel
            </button>
            <button type="submit" className="submit-button" disabled={loading}>
              {loading ? "Creating Story..." : "Create Story"}
            </button>
          </div>
        </form>
      </div>
    </Layout>
  )
}

export default CreateStoryPage
